css:
@-moz-document domain('dilidili.com'), domain('dilidili.wang'), domain('dilidili.name'), 
  domain('dilidili.mobi') {
  body,
.container,
.bb-comment,
.container-row-1,
.container-right-item,
.con24,
.num a,
.build-floor-gw,
.bulid-middle-hide-floor-dw a,
.con20,
.week_mend,
#carousel,
.nav_nav {
    background: #111 !important;
}
div.block-cont-hover-e {
    background: #111919 !important;
}
div.swiper-slide ul li {
    color: #111;
}
body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
.num a,
.player_sx p a,
.pk_year a.current,
.tooltipster-content p {
    color: #eee;
}
.player_switch li a {
    background: #111;
}
#link a,
#intro2 p,
.bb-comment .comment-list .list-item .user .name,
.small.book figcaption p:nth-of-type(2) {
    color: #bbb;
}
a {
    color: #eee;
}
.tooltipster-box {
    background: #734 !important;
    color: #eee !important;
}
.tooltipster-arrow-border {
    border-right-color: #734 !important;
}
input,
#playhistory li {
    background: #555 !important;
    color: #eee !important;
}
#big_gonggao,
.weibo-container {
    display: none;
    visibility: hidden;
}
}
