css:
@-moz-document domain("soundgasm.net") {
body {
    background-color: rgb(35, 34, 35);
    margin: 40px;
    /* font: 13px/20px normal Helvetica, Arial, sans-serif; */
    font-family: Ubuntu, sans-serif;
    color: rgb(173, 173, 173);
    align-content: center;
}

a:link {
    color: red;
}

/* visited link */
a:visited {
    color: #ff2512;
}

/* mouse over link */
a:hover {
        transition: all 0.5s ease;
    color: hotpink;

}



/* selected link */
a:active {
    color: #580b0b;
}



margin.a {
    color: #fff;
    text-decoration: underline;
    cursor: auto;
}


.logo {
    height: 90px;
    display: inherit;
    text-decoration: none;
    color: #848484;
}
nav > a {
    /* width: 80px; */
    color: #988585;
    background-color: rgba(0, 0, 0, 0.08);
    font-weight: 500;
    font-family: Ubuntu, sans-serif;
    font-size: 16px;
    text-align: center;
    text-decoration: none;
    padding: 5px 20px;
}

element.style {
    color: #921212;
}

.jp-audio, .jp-audio-stream, .jp-video {
    font-size: 16px;
    font-family: Verdana, Arial, sans-serif;
    line-height: 1.6;
    color: #ac2223;
    border: 1px solid #009be3;
    border-radius: 4px;
    background-color: #000;
}
.jp-details, .jp-playlist {
    width: 100%;
    background-color: #3e3e3e;
    border-top: 1px solid #009be3;
}


.jp-description {
color: #FFF;
}


.jp-toggles button {
    display: block;
    float: right;
    width: 25px;
    height: 18px;
    text-indent: -9px;
    line-height: 100%;
    border: none;
    cursor: pointer;
}

h1 {
    color: #a04b4b;
    background-color: rgba(105, 101, 101, 0.39);
    border-bottom: 1px solid #D0D0D0;
    font-size: 19px;
    font-weight: normal;
    margin: 0 0 14px 0;
    padding: 14px 15px 10px 15px;
}


input, textarea {
    font-size: 12px;
    padding: 4px 0;
    border: solid 1px #aacfe4;
    width: 100%;
    margin: 2px 0 20px 0;

    background-color: #696969;
}



/*------------Player -------------*/
.jp-play {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 0 no-repeat;
}

.jp-play:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) -41px 0 no-repeat;
}
.jp-state-playing .jp-play {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -42px no-repeat;
}

.jp-state-playing .jp-play:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) -41px -42px no-repeat;
}
.jp-stop {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -83px no-repeat;
    margin-left: 10px;
}
.jp-stop:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) -29px -83px no-repeat;
}

.jp-repeat {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -290px no-repeat; 
}
.jp-repeat:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -290px no-repeat; 
}
.jp-mute {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -170px no-repeat;
}

.jp-mute:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -170px no-repeat;
}
.jp-volume-bar-value {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -256px repeat-x;
    width: 0px;
    height: 5px;
}
.jp-volume-max {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -186px no-repeat;
}
.jp-volume-bar-value:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -256px repeat-x;
    width: 0px;
    height: 5px;
}
.jp-volume-max:focus {
    background: url(http://i.imgur.com/AGkHebw.jpg) 0 -186px no-repeat;
}



.jp-seek-bar {
  background: url("http://i.imgur.com/AGkHebw.jpg") 0 -202px repeat-x;
  width: 0px;
  height: 100%;
  cursor: pointer; }

.jp-play-bar {
  background: url("http://i.imgur.com/AGkHebw.jpg") 0 -218px repeat-x;
  width: 0px;
  height: 100%; }



/*------------Player -------------*/
}
