css:
@-moz-document domain("www.shsh.ylc.edu.tw") 
{
#block-md-slider-shsh-index,#block-block-14,.content>iframe,#block-block-21,.home-intro,.page-top{
display:none;
}
#header{
	background: #FFF;
    border-top: 0px;
}
.shshweb-table th {
    border: 0px solid #ffffff;
    background: #3e3e4a;
}
div.home-intro{
    background-color: #f0f0f0 !important;
    color: #000 !important;
}
#logo{
	background-image: url(/*[[logo]]*/);
    background-size:277.58px 60px;
}
#logo>img{
	opacity: 0;
}
.panel-group.secundary .panel-heading a {
    color: #FFF;
    background: #3e3e4a;
}
#header nav ul.nav-main ul.dropdown-menu, #header nav ul.nav-main li.dropdown.open .dropdown-toggle, #header nav ul.nav-main li.active a, #header nav ul.nav-main li > a:hover, #header nav ul.nav-main li.dropdown:hover a {
    color: #FFF;
    background: #3e3e4a  !important;
}
.btn-primary, .pagination > .active > a{
  cursor: pointer;
  padding: 0.78571em 1.5em;
  border-radius: 0.28571rem;
  font-size: 1em;
  font-weight: 700;
  display: inline-block;
  line-height: 1.2142em;
  border-width: 1px;
  border-style: solid;
  vertical-align: middle;
  text-align: center;
  position: relative;
  border-color: #eeeeee;
  background-color: #F1F1F1;
  background-image: linear-gradient(to bottom, #F1F1F1, #F1F1F1);
  color: #7B7B7B;
}
a {
    color: #3e3e4a;
}
li > a{
	  color: #3e3e4a;
}
.scroll-to-top {
    transition: all 0.1s;
    background: rgba(0, 0, 0, 0.44);
}
.shshweb-table td {
    border:  0px;
}
#header nav ul.nav-main ul.dropdown-menu > li > a:hover {
    color: #FFF;
    background: rgba(255,255,255,0.2) !important;
    font-weight: 400;
    padding-bottom: 4px;
    padding-top: 4px;
    position: relative;
    padding: 8px 10px 8px 5px;
    font-size: 0.9em;
}
.PAGE{
	margin: 0px auto;
}
body{
    font-family: "Open Sans","Noto Sans TC","Meiryo","微軟正黑體","Microsoft JhengHei",sans-serif !important;
    background-color:#FFF;
}
.home-intro p em {
    font-family: "Open Sans","Noto Sans TC","Meiryo","微軟正黑體","Microsoft JhengHei",sans-serif !important;
}
#footer {
    background: #3e3e4a;
    border-top: 4px solid #3e3e4a;
    font-size: 1.0em;
    margin-top: 50px;
    position: relative;
    clear: both;
}
#footer .footer-copyright {
    background: rgba(12, 12, 12, 0.38);
    border-top: 4px solid rgba(12, 12, 12, 0);
    padding: 30px 0 10px;
}
#footer .footer-ribbon {
    background: #818195;
}
@media (min-width: 992px){
	#content>.container>.row>.col-md-6 {
	    width: 75%;
	}
}
}
