css:
@-moz-document url-prefix("https://my.nintendo.com") {
.MFooter {
    background-color: #ffcc00 !important;
    background-image: -webkit-linear-gradient(bottom, rgba(0, 0, 0, .1), transparent 110px);
    background-image: linear-gradient(0deg, rgba(0, 0, 0, .1), transparent 110px);
    position: relative;
    overflow: hidden;
}
.NFooter {
    position: relative;
    background-color: #ffcc00 !important;
    overflow: hidden;
}
.MissionFilter {
    background-color: #ffcc00 !important;
    overflow: hidden;
}
.MHeader {
    display: table;
    position: relative;
    width: 100%;
    height: 140px;
    padding: 0 20px;
    background-color: #ffcc00 !important;
    color: #fff;
    table-layout: fixed;
    z-index: 0;
}
.CurrentPoints_nav .mynintendo-icons {
    height: 32px;
    width: 32px;
    border-radius: 16px;
    background-color: #ffcc00 !important;
    line-height: 32px;
    text-align: center;
}
.MiiStage_bg {
    position: absolute;
    width: 100%;
    height: 325px;
    background-color: #ffcc00 !important;
}
.RewardItem-exchangeable .RewardItem_exchangeStatus {
    color: #ffcc00 !important;
}
.MainNav_button {
    display: table;
    width: 347px;
    height: 112px;
    -webkit-transition: opacity .2s;
    transition: opacity .2s;
    border-radius: 56px;
    background-color: #fff;
    color: #ffcc00 !important;
    box-shadow: 0 4px 0 0 #cfcfcf;
    table-layout: fixed;
}
.en-US .MainNav_button.getPoint .text:after {
    background-image: url(http://dc703.4shared.com/img/covGyJ45ba/s24/15a03001730/en-usd081b097938e74a5?async&rand=0.8116952359240381) !important;
    background-repeat: no-repeat;
}
.en-US .MainNav_button.usePoint .text:after {
    background-image: url(http://dc703.4shared.com/img/Co3j37Ibce/s24/15a03001f00/en-usd09042616b27f500?async&rand=0.024026245302896543) !important;
    background-repeat: no-repeat;
}
.Tag-new {
    background-color: #ffcc00 !important;
    color: #fff;
}
.PageSubHeader_title {
    font-weight: 700;
    margin: 8px 0;
    color: #ffcc00 !important;
    font-size: 1.66667em;
    line-height: 1.3;
    white-space: pre-line;
}
.PageSubHeader_line line {
    stroke: #ffcc00 !important;
    stroke-width: 6;
    stroke-dasharray: 0, 16;
    stroke-dashoffset: -3;
    stroke-linecap: round;
}
.RewardDetail_subtitle .mynintendo-icons {
    position: relative;
    top: -1px;
    margin-right: 4px;
    color: #ffcc00 !important;
    font-size: 12px;
}
.RewardDetail_enclose a {
    color: #ffcc00 !important;
    text-decoration: underline;
}
.btn-primary:hover {
    background-color: #E0B400 !important;
}
.News_archiveButton {
    display: inline-block;
    padding: 19px 20px 17px;
    -webkit-transition: background-color .2s;
    transition: background-color .2s;
    border-bottom: 4px solid #c09900 !important;
    border-radius: 30px;
    background-color: #ffcc00 !important;
    color: #fff;
}
.NewsItem_date {
    position: absolute;
    right: 20px;
    bottom: 15px;
    font-weight: 700;
    display: block;
    color: #ffcc00 !important;
    font-size: 18px;
    line-height: 1;
    text-align: right;
}
.MFooter_character .mario {
    display: block;
    width: 230px;
    height: 188px;
    background-image: url(http://dc592.4shared.com/img/t4NHc7l0ba/s24/15a0d8aa350/partsad2be143d674db2a?async&rand=0.827643618216727) !important;
    background-repeat: no-repeat;
    background-position: 0 0;
    background-size: 370px 270px;
    text-indent: -9999px;
    overflow: hidden;
    margin: 0 auto;
}
.SecondaryNav {
    padding: 0 30px;
    border-bottom: 8px solid #fff;
    background-color: #ffcc00 !important;
    color: #fff;
    text-align: center;
}
.SecondaryNav_item.current {
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    position: relative;
    background-color: #fff;
    color: #ffcc00 !important;
}
.SecondaryNav_item.current a {
    color: #ffcc00 !important;
}
.SecondaryNav_itemCurrentLine line {
    stroke: #ffcc00 !important;
    stroke-width: 6;
    stroke-dasharray: 0,14;
    stroke-dashoffset: -3;
    stroke-linecap: round;
}
.PointExpiration_expireDate {
    font-weight: 700;
    display: table-cell;
    padding: 0 17px 0 30px;
    color: #ffcc00 !important;
    font-size: 1.16667em;
    vertical-align: middle;
}
.PointHistoryItem_pointItem-plus {
    color: #ffcc00 !important;
}
.PointHistoryItemDivider {
    font-weight: 900;
    position: relative;
    margin: 51px auto 17px;
    padding-left: 40px;
    color: #ffcc00 !important;
    font-size: 34px;
    list-style: none;
}
.form-select .mynintendo-icons {
    position: absolute;
    top: 50%;
    -webkit-transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    transform: translateY(-50%);
    right: 12px;
    color: #ffcc00 !important;
    font-size: 23px;
    z-index: 1;
    pointer-events: none;
}
.Pager_item .mynintendo-icons {
    color: #ffcc00 !important;
    font-size: 1.33333em;
}
.btn-inverse {
    border-color: #ffcc00 !important;
    background-color: #fff;
    color: #ffcc00 !important;
    padding-top: 8px;
    padding-bottom: 8px;
    -webkit-transition: background-color .2s;
    transition: background-color .2s;
    border-width: 3px;
}
.MissionItem_progress .steps {
    font-weight: 700;
    position: absolute;
    margin-top: -4px;
    margin-left: -35px;
    color: #ffcc00 !important;
    font-size: 10px;
}
.MissionItem_progressBar .mynintendo-icons-flag-1 {
    position: absolute;
    top: 0;
    left: 100%;
    margin-top: -7px;
    color: #ffcc00 !important;
    font-size: 18px;
}
.MissionItem_progressBar .colorBar {
    position: absolute;
    height: 100%;
    border-radius: 2px;
    background-color: #ffcc00 !important;
}
element.style {
    background-color: #ffcc00 !important;
}
.Modal_header {
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    padding: 15px;
    background-color: #ffcc00 !important;
    color: #fff;
    text-align: center;
}
.btn-primary {
    border-color: transparent;
    background-color: #ffcc00 !important;
    color: #fff;
    position: relative;
    -webkit-transition: background-color .2s;
    transition: background-color .2s;
}
.BannerSwipe .BannerSwipe_next:hover:not(.swiper-button-disabled) .mynintendo-icons, .BannerSwipe .BannerSwipe_prev:hover:not(.swiper-button-disabled) .mynintendo-icons {
    background-color: #ffcc00 !important;
    color: #fff;
}
.BannerSwipe .BannerSwipe_next .mynintendo-icons, .BannerSwipe .BannerSwipe_prev .mynintendo-icons {
    height: 20px;
    width: 20px;
    -webkit-transition: background-color .2s,color .2s;
    transition: background-color .2s,color .2s;
    border-radius: 10px;
    background-color: #fff;
    color: #ffcc00 !important;
    font-size: .83333em;
    line-height: 20px;
}
.MissionBulkReceive_header {
    font-weight: 700;
    display: inline-block;
    margin: 0 20px 0 0;
    color: #ffcc00 !important;
}
.SiteNavPanelMenu-main .SiteNavPanelMenu_item:hover {
    color: #ffcc00 !important;
}
.MissionBulkModal_pointTransition .currentPoint {
    display: table-cell;
    padding-bottom: 2px;
    color: #ffcc00 !important;
    font-size: 3em;
    text-align: right;
    vertical-align: middle;
}
.MissionBulkModal_link .mynintendo-icons {
    margin-right: 8px;
    color: #ffcc00 !important;
    font-size: 1.33333em;
    vertical-align: -4px;
}
.RewardFilter:hover {
    color: #ffcc00 !important;
}
.RewardDetail_attention .body {
    display: inline-block;
    color: #ffcc00 !important;
}
.MoreRewards_end .target {
    display: block;
    position: relative;
    height: 0;
    padding-top: 100%;
    padding-bottom: 140px;
    -webkit-transition: background-color .2s,color .2s;
    transition: background-color .2s,color .2s;
    border-radius: 16px;
    color: #ffcc00 !important;
    text-align: center;
}
.DirectNav a:hover {
    color: #ffcc00 !important;
}
.NFooter .copyright:after {
    color: #ffffff;
    content: "  Gold My Nintendo - v1.0 by NickCox01";
    font-weight: bold;
    font-size: medium;
}
}
