#tinymce.mce-content-body blockquote a.unclean {
    background-color: purple;
    color: #efefef;
}
div.postcontent blockquote a.btn-primary,
div.postcontent blockquote a.label-info,
div.postcontent blockquote a.btn-danger,
div.postcontent blockquote a.btn-warning,
#tinymce.mce-content-body blockquote a.btn-primary,
