    */
    
    color: purple;
    background-color: #efefef;
}
div.postcontent blockquote p.postedby ~ blockquote,
#tinymce.mce-content-body blockquote p.postedby ~ blockquote {
    border: 1px dashed #666;
}
div.postcontent blockquote blockquote p.postedby,
#tinymce.mce-content-body blockquote blockquote p.postedby {
    background-image: none;
    color: #333;
    display: inline-block;
    padding: 4px;
    background-color: inherit;
    border: 0;
    margin-bottom: 4px
}
div.postcontent blockquote blockquote p.postedby>a,
#tinymce.mce-content-body blockquote blockquote p.postedby>a {
    text-decoration: none;
    font-weight: bold;
    color: #333
}
div.postcontent blockquote blockquote p.postedby a.posterlink,
#tinymce.mce-content-body blockquote blockquote p.postedby a.posterlink {
    text-decoration: none;
    font-weight: bold;
    color: #333
}
.postcontent blockquote p.postedby,
#tinymce.mce-content-body blockquote p.postedby {
    font-size: 13px
}
blockquote {
    /*
    padding: 10px 20px;
