@-moz-document regexp("http:\\/\\/(jwxw\\.gzcc\\.cn|202\\.116\\.16\\d\\.\\d{1,3})\\/(default\\d*\\.aspx)?") {
#form1 {
    width: 100% !important;
    height: 100% !important;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.5) 43px, rgba(255, 255, 255, 0.4) 92px, rgba(255, 255, 255, 0.3) 146px, rgba(255, 255, 255, 0.2) 210px, rgba(255, 255, 255, 0.1) 295px, rgba(255, 255, 255, 0) 500px), url(https://unsplash.it/1920/1080/?random)!important;
    position: fixed !important;
}

dl {
    margin: 0 !important;
    width: 100% !important;
}

dt {
    display: block;
    float: left;
    width: 75px !important;
}

dd {
    display: block;
    float: left;
    width: 180px !important;
}

#txtUserName {
    width: 100% !important;
}

#TextBox2 {
    width: 100% !important;
}

.login_logo {
    background: none !important;
    display: none !important;
}

.login_left {
    display: none !important;
}

.login_right {
    background: none !important;
    font-size: 16px !important;
    text-shadow: 2px 2px 4px #000000 !important;
    color: white !important;
    position: fixed;
    top: 60%;
    left: 70%;
    width: 256px;
    transform: translateX(-50%) translateY(-50%) !important;
}

.login_right input {
    font-size: 16px !important;
}

.login_right .uesr {
    background: none !important;
    text-indent: 0px !important;
}

.login_right .passw {
    background: none !important;
    text-indent: 0px !important;
}

.login_right .yzm {
    background: none !important;
    text-indent: 0px !important;
}

.login_copyright {
    position: fixed !important;
    left: 50% !important;
    top: 100% !important;
    width: 80% !important;
    transform: translateX(-50%) translateY(-45px) !important;
}

.login_copyright span {
    color: white !important;
    margin: 0 !important;
    padding: 0 !important;
}

.btn_dl:after {
    content: "login";
}

input#txtSecretCode {
    width: 50% !important;
}

img#icode {
    top: inherit !important;
    left: inherit !important;
    right: 0px !important;
}

a#icodems {
    color: #11449e !important;
    width: 100px !important;
    top: 36px !important;
    left: inherit !important;
    right: -28px !important;
}

#form1 > div > div.login_right > dl:nth-child(7) > dd {
    width: 100% !important;
}

table#RadioButtonList1 {
    width: 100%;
}

#form1 > div > div.login_right > dl:nth-child(7) {
    transform: translateY(-20px);
}

#form1 > div > div.login_right > dl:nth-child(9) {
    transform: translateY(-50px);
}
}

@-moz-document regexp("http:\\/\\/(jwxw\\.gzcc\\.cn|202\\.116\\.16\\d\\.\\d{1,3})\\/(pyjh|mmxg|tjkbcx|x.+)\\.aspx.*") {
body {
    border: none !important;
    color: #4d76ab !important;
}

button,
.button {
    background: #4183c4 !important;
    box-sizing: border-box !important;
    color: #ffffff !important;
    outline: none !important;
    border-radius: 4px !important;
    padding-left: 4px !important;
}

.toolbox {
    background: none !important;
}

.toolbox > div.buttonbox {
    visibility: hidden;
}

.toolbox > p {
    visibility: hidden;
}

.main_box {
    background: none !important;
}

.main_box .title {
    visibility: hidden;
}

.mid_box {
    background: none !important;
}

.footbox {
    visibility: hidden;
}

.datelist thead th,
.datelist thead td,
.datelisthead td,
.datelisthead td:hover {
    background: lightgrey !important;
}

.datelist th a,
.datelist td a {
    color: #4d76ab !important;
}
}

@-moz-document regexp("http:\\/\\/(jwxw\\.gzcc\\.cn|202\\.116\\.16\\d\\.\\d{1,3})\\/xs_main\\.aspx.*") {
body {
    border: none !important;
    color: #4d76ab !important;
}

#headDiv > div.head {
    background: none !important;
}

#headDiv > div.head > div {
    display: none;
}

.info {
    position: fixed !important;
    top: 0px !important;
    right: 0px !important;
    background: none !important;
    z-index: 100 !important;
    width: 200px !important;
    color: white !important;
    text-shadow: 2px 2px 4px black !important;
}

.info a {
    color: white !important;
    text-shadow: 2px 2px 4px black !important;
    font-weight: bold;
}

#headDiv > ul {
    position: fixed !important;
    top: 0px !important;
    background: none !important;
    height: 30px !important;
    z-index: 0 !important;
    margin-top: 10px !important;
}

#headDiv > ul > li > a {
    height: inherit !important;
    background: none !important;
    padding: 0px !important;
}

#headDiv > ul > li > a > span {
    height: inherit !important;
    background: none !important;
    font-size: 18px !important;
    color: white !important;
    font-weight: normal !important;
    text-shadow: 2px 2px 4px black;
}

.nav:hover ul.sub {
    background: none !important;
    border: none !important;
    font-size: 16px !important;
    padding-top: 10px;
    transition: 0.3s;
}

.nav:hover ul.sub li {
    height: 32px !important;
}

.nav:hover ul.sub li a {
    color: white !important;
    text-shadow: 2px 2px 4px black !important;
    text-indent: 8px !important;
    height: 32px !important;
}

.nav:hover ul.sub li a:hover {
    background: none !important;
    text-shadow: 2px 2px 4px white !important;
    color: grey !important;
}

.nav li a.top_link:hover {
    background: none !important;
}

.nav li a.top {
    height: inherit !important;
    background: none !important;
    padding: 0px !important;
}

.nav li a.top:hover {
    background: none !important;
}

#headDiv {
    height: 80px !important;
    background: linear-gradient(to top, rgba(250, 250, 250, 1), rgba(250, 250, 250, 0.2)), url(https://unsplash.it/1920/1080/?random), white!important;
    background-position: top center!important;
    background-size: cover;
    background-attachment: fixed;
    background-repeat: no-repeat;
    border: none;
    box-shadow: 0 0 15px 5px #fafafa!important;
    ;
}

#headDiv > div.tab > p {
    margin-top: 52px;
}

#bodyDiv>#mainDiv {
    top: 85px !important;
}

#footerDiv > div {
    background: none !important;
}
}

