@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodreads.com") {
  
  /***** Inherited from Goodreads dark theme dated Feb 5, 2017 ******/
  
  body, #signedOutHome #siteheader {
    background: rgb(22,22,22);
    color: var(--default-color);
  }

  .home #siteheader, #siteheader, .grey500Box, .h2Container, .footerContainer, #addNewBook, #hp_status_editor .updateStatusLink.open, div.navigationBox, .shelfChooser .chooseShelvesLink, .siteHeader, .dropdown__menu--notifications, .dropdown__menu--siteHeader, .siteHeader__subNav--profile, .searchResults, .datum {
    background: rgb(30,30,30);
    border-bottom: 1px solid rgb(20,20,20);
  }

  .selectedTab, .tab:hover, input#sitesearch_field, .auto_complete_results, .dropDownMenu, .gr-button, .subnavlink, input[type="password"], input[type="email"], input[type="text"], textarea, .button, .errorBox, .noticeBox, .flash.error, .ctaBannerShown, .formSubmissionNotification, .gr-form--compact__submitButton, .wtrButtonContainer .wtrShelfMenu, .wtrButtonContainer .wtrLeft.wtrDown, .wtrButtonContainer .wtrRight.wtrDown, .borderRow, .borderMiddle, .wtrNonExclusiveShelves label:hover, .wtrButtonContainer .wtrShelfMenu label.wtrExclusiveShelf:hover, .wtrButtonContainer .wtrShelfMenu > .wtrShelfList > ul.wtrExclusiveShelves > li:hover, .buttonBar, .floatingBox, .gr-form__submitButton, .rating_graph, .searchBox--large__button, .wtrButtonContainer .wtrPrompt, .h1Shelf, .h1Shelf a, .selectedTab.tabHeader, .answerButton:hover, .jobsValuesButton, .notInterestedMessage, .user_status_review_lightbox .userStatus form, .user_status_review_lightbox .updateStatusLink.open, .graphBar, .user_status > td > .left > .left > .left, li.favoriteAuthor:hover, #pagestuff.infiniteFooter .buttons, .wantToReadButton__leftUnshelved, .datum:hover {
    background: var(--lighter-bkgd-color)!important;
    background-color: var(--lighter-bkgd-color)!important;
    color: var(--default-color)!important;
    border: 1px solid var(--default-border-color)!important;
  }

  .siteHeader__spotlight--browseMenu:before {
    background: var(--default-bkgd-color);
  }

  .featuredGenres__genreSpotlight:before, .genreList__genreLink:hover, .searchResults--navbar__item:hover {
    background: var(--default-border-color);
  }

  .tab, .auto_complete_result.selected, .runQuery.selected, .wtrToRead, .wtrUp, #updateTypeFilters, #updateToggle, .grDropdownMenu, .contentBox, #box, .wtrButtonContainer .wtrShelfMenu, .communityQuestion .fullQuestionBody, .goodreads, form.challengeForm.newChallengeForm, .tooltip, .greyBox, .searchBox--large, div.navigationBox div.navRow, .answerButton, .jobsValuesBox, .jobsValuesButton, .aboutMediaBox, .brownBox, .user_status_review_lightbox .graphContainer, .userStatus form, .reorderControls a strong, .reorderControls a img, .authorDropdownMenu, .controlGroup .open, .controlBody, #fav_authors .section, .extrastats, .bookchart .books {
    background-color: var(--default-bkgd-color)!important;
    color: rgb(172,172,192)!important;
    border: 1px solid rgb(25,25,25)!important;
  }

  .wtrButtonContainer .wtrShelfMenu button.wtrExclusiveShelf:hover, .prototip_CornerWrapper, .toolbar, div.navigationBox div.navRow a:hover {
    background: var(--lighter-bkgd-color)!important;
    background-color: var(--lighter-bkgd-color)!important;
    color: var(--default-color)!important;
  }

  #signedOutHome #discoveryBox {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }

  .home #siteheader, #siteheader, .grey500Box, .grey500BoxBody, .grey500BoxBottom, h2.brownBackground, .caretArrow, .wtrToRead, .wtrUp, .tfooter, .bigGreyBox, .bigGreyBoxBody, .bigGreyBoxBottom, div.prototip_StemImage, .standardGradient, .bigTabs .tab, .mediumTabs .tab, #signedOutHome .mainContentContainer, #signedOutHome .discoveryBoxDiscovery, .big420BoxBody, .big420Box, .big420BoxBottom, .ctaBannerTriangle {
    background-image: none!important;
  }

  H1, .infoBoxRowTitle, h2.brownBackground a, .h2Container H2, #siteheader a, #siteHeader .exploreMenu a, .by, .grDropdownMenu a, label, th, .wtrButtonContainer .wtrShelfMenu li, .actionLinkLite, .communityQuestionSubTitle, .questionText a, #personalSite, .toolbar .title, .wtrButtonContainer .wtrPrompt a, .tableList th a, .pageHeader, .quizQuestionText, h2, p, .aboutMetric, li, .aboutRightColumnWide section, .myRating, #homeRecsBox, .userReview, .dataTitle, .columnHeader, .gr-book__author, .siteHeader__title, .updateAction {
    color: var(--default-color)!important;
  }

  .shelfChooser .shelves li.hover, li.sortable_item:hover, .shelfChooser .shelves li:hover, .shelfChooser .newShelfButtonLi:hover, .siteHeader__topLevelLink:hover, .dropdown__trigger--activeSiteHeader, .dropdown__trigger--personalNavActive, .headerPersonalNav:hover {
    background-color: var(--default-bkgd-color);
  }

  .home #siteheader {
    padding-top: 0px;
  }

  .bigTabs, .tableList td, #description, #details, .communityQuestionAndAnswer, #bookReviewControls, .tabBoxes, .shareBarContainerDarkBorder, .actionLinkLites, .shelfChooser .shelves li.exclusive {
    border-bottom: 1px solid var(--default-border-color)!important;
  }

  #updateTypeFilters .header, .editGenres, .grDropdownMenu .grDropdownMenuItem, .elementList, .shelfStat, .quote, .mediumTabs, #leadercol, #books.table tbody tr, #paginatedShelfList > div:nth-child(4), h2.brownBackground, .userProfileName, .gr-notification, .notificationWrapper, .searchResults--navbar__item, #descriptionContainer, .bookchart {
    border-bottom: 1px solid var(--lighter-bkgd-color)!important;
  }

  .typeFilters, .editGenres, .wtrButtonContainer .wtrShelfMenu > .wtrOtherShelfOptions, .Updates, .listElement, .gr-moreNotifications, .gr-notification {
    border-top: 1px solid var(--lighter-bkgd-color)!important;
  }

  .tableListReverse td, .elementListBrownSeparator, .shareBarContainerDarkBorder, .aboutDividerLight, .shelfChooser .newShelfButtonLi, .noQuestionsMessage {
    border-top: 1px solid var(--default-border-color);
  }

  .rightBorder, .wtrButtonContainer .wtrShelfMenu > .wtrOtherShelfOptions > label.wtrAddShelf, #side, .tabBoxes {
    border-right: 1px solid var(--default-border-color);
  }

  .tabBoxes {
    border-left: 1px solid var(--default-border-color);
  }

  .siteHeader__spotlight--browseMenu:before {
    border-left: 1px solid rgb(30,30,30);
  }

  .actionLinkLites {
    border-top: solid 5px var(--default-border-color)!important;
  }

  .profileLinks {
    border-top: 2px solid var(--default-border-color)!important;
  }

  .searchSubNavContainer {
    border-bottom: 2px solid var(--default-border-color);
  }

  .tableList th {
    border-color: -moz-use-text-color -moz-use-text-color var(--default-border-color)!important;
  }

  .box, .rightedBox {
    border: 2px solid var(--default-border-color);
  }

  #community_question_question_text, #community_answer_answer_text, .shelfChooser .chooseShelvesLink {
    border: 1px solid var(--default-border-color);
  }

  .floatingBox, .button:active, .button, .jobsValuesBox, #signedOutHome #newAccountBox .button, input[type="text"], input[type="email"], * {
    box-shadow: none!important;
  }

  .rating_graph span, .gr-button--invisible, .gr-button--invisible:hover {
    border: none!important;
  }

  .tooltipCaret--left {
    border-right: none;
  }

  .datum {
    border-radius: 0;
  }

  .communityQuestion .triangle {
    border-color: var(--default-bkgd-color) transparent transparent;
  }

  .wtrButtonContainer .wtrPrompt:after, .wtrButtonContainer .wtrPrompt:before {
    border-color: var(--lighter-bkgd-color) transparent;
  }

  .tooltipTrigger .tooltip .tooltipCaretLeft {
    border-right: 10px solid #232323;
    left: -10px;
  }

  #signedOutHome .featureTeaserBox .quizBox, .listTeaser {
    border-color: var(--lighter-bkgd-color)!important;
  }

  body#signedOutHome.home > div.content > div.mainContentContainer > div.mainContent > div.mainContentFloat > div.leftContainer {
    position: relative;
    top: -285px;
  }

  .mainContentFloat > h1:nth-child(2) > img:nth-child(1), div#headline, .prototip_CornerTr, .prototip_CornerBr, .fader, #pagestuff.infiniteFooter:before, #self_serve_ads, #cobrandSlot, #premiumAdTop, img.img_ad, div.doubleStacked {
    display: none;
  }

  .brown_comment {
    background-color: #111 !important;
    border-color: #444 !important;
  }

  #siteheader #usernav li.generalNotice a.icon {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAMAAAAoLQ9TAAAARVBMVEUAAABkUkBkUkBkUkBkUkBkUkBkUkBkUkBtXErq6dvY1cfy8uWimIi0rJ29tqiIeml%2FcF92ZlWropOZjn7Pyrzh39GRhHSMRJlJAAAAB3RSTlMAYNAQ8LDAThnSHAAAAHhJREFUeF5lz0mugzAAA9AkQF4GZvr%2F%2FY9aobKoVC%2Ffxna4EyeYYvhkSABpuGEYsZ1z2RhvSVjqsrSOFELE0XfmDjFkzA2lQw5wvqCDAFfF9gWlro72BfZX%2F7seyCgb9gr5qZ2VtkL8DPtvva4F6ZkOMA6%2F537uvwGpugbVLOx6dAAAAABJRU5ErkJggg%3D%3D');
  }

  #siteheader #usernav li.inboxNotice a.icon {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8%2F9hAAAAiElEQVR42sWT4QmAIBBGbzOjCaIF7FdruIIbOEIjNEKj3AjXHXxCPwypIxIeeKffQ0FJRFz4Bes8Dgor8hC2rAkWpbwQFMuSFZCkB%2BGEjNClmZXYC2NPrjU1jhUUvrlzqNdtCsCOjVvtYR6wJj2BcSgTYdgcPekIOnwlYIeATRAdLzG6%2F8L%2FghO59DY3DPYcMQAAAABJRU5ErkJggg%3D%3D');
  }

  #siteheader #usernav li.friendsNotice a.icon {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8%2F9hAAAAtElEQVQ4y2NgAIK0ICcBIF4AxO%2BB%2BD6ULcBALAAq3g%2FE%2F9HwelIM%2BI8NI8k3QF0Hwg3omhVwGPAeKuePRc4f2QB9XC4AYnkgrsciXo%2FuCmyK8vHIYRjAD3UyTMF9AgG8H1tAnkdScB4q5o%2FHeyjhkI9FQQKSF96jBW49sub5eGyZgMt1BNMAEp6Py3XEGoDXdQzQtE%2BuIQboCcoeikHsfiIMyMeXP%2BwJuA4kZ09sZkNxHbIcAJgDSHOUA52BAAAAAElFTkSuQmCC');
  }

  a, .u-defaultType {
    color: rgb(124,0,0) !important;
  }
  
  /***** by Abhishek Chakraborty ******/
  
  :root {
    --default-bkgd-color: rgb(35,35,35);
    --lighter-bkgd-color: rgb(50,50,50);
    --default-color: rgb(172,172,172);
    --default-border-color: rgb(40,40,40);
  }
  
  /* Top Logo */
  .siteHeader__logo {
    filter: invert(50%) !important;
  }
  
  /* Link Color */
  a, .u-defaultType {
    color: #047fd5 !important;
  }
  
  /* Search Drop-down hover */
  .searchResults__item--active {
    background-color: var(--lighter-bkgd-color);
  }
  
  /* Group Discussions */
  .dropdown__menu--discussions { /* , .dropdown__menu--notifications */
    background-color: var(--default-bkgd-color);
  }
  
  .siteHeader__heading {
    color: var(--default-color);
  }
  
  /** Top right Icons **/
  
  .headerPersonalNav__icon--notifications { filter: opacity(50%); }
  .headerPersonalNav__icon--discussions { filter: opacity(40%); }
/*   .headerPersonalNav__icon--inbox { }
  .headerPersonalNav__icon--friendRequests { } */
  .circularIcon { filter: opacity(60%); }
  
  /* Icons hover */
  html.no-touchevents .headerPersonalNav__icon--notifications:hover, 
  html.no-touchevents .dropdown__trigger--personalNav:hover {
    background-color: var(--default-border-color);
  }
  
  /*** Home ***/
  
  .gr-homePageBody {
    background-color: var(--default-bkgd-color);
  }
  
  /* Updates */
  .gr-newsfeedItem {
    background-color: var(--lighter-bkgd-color);
    border-color: var(--default-border-color);
  }
  
  .gr-newsfeedItem__details {
    background-color: var(--lighter-bkgd-color);
  }
  
  /* Customize - Updates Feed Preferences */
  .gr-popover {
    background-color: var(--default-bkgd-color);
  }
  
  /* Headings e.g. 'Currently Reading', 'Updates', etc. */
  h3 {
    color: var(--default-color);
  }
  
  /* Recommendations General Update */
  .longTextPopupForm {
    background-color: var(--default-bkgd-color);
  }
  
  .u-beigeBackground {
    background-color: var(--default-bkgd-color);
    border-bottom-color: var(--default-border-color);
  }
  
  /* Ads */
  a.adContent-clickOverlay {
    background: var(--default-bkgd-color) !important;
  }

}
