css:
@namespace url(http://www.w3.org/1999/xhtml);
/* Board Fixer!
/* v0.4
*/

@-moz-document regexp(".*board.*nostale\.gameforge\.com.*"){
    
    /* Ograniczenie szerokości */
    .layoutFluid {
        max-width: 1400px;
    }
    
    /* Ogólna całość */
    .body {
        font-family: "Trebuchet MS", sans-serif;
        font-size: 13px;
    }
    
    /* Szukajka */
    .userPanel > div > .searchBar > form input[type="search"] {
        border-radius: 0px;
    }
    
    /* Numerki */
    .badge {
        padding: 0px 6px 0px;
        box-shadow: none;
    }
    .userTitleBadge{
        padding: 1px 6px 0px;
    }
    h3 > .badgeUpdate {
        padding: 0px 5px 0px;
    }
    
    /* Obrazek pod rangą */
    .sidebarBoxHeadline > p:nth-child(3) {
        padding-top: 4px;
    }
    
    /* Miejsce loga */
    .logo {
        margin-bottom: 10px;
    }
    .userPanel + .logo {
        margin-top: 50px;
    }
    
    /* Ogólny font na stronie */
    [id*='tpl']{
        font: normal .85rem tahoma,helvetica,arial,sans-serif;
    }
    
    /* Napis "Nowy" przy postach etc. */
    .newMessageBadge {
        font-family: monospace;
    }
    
    /* Belki działów */
    .wbbCategory > header {
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    }
    
    /* Rozwijane menu */
    .open {
        /*Porusza się ze stroną */
       position: fixed;
       top: 40px !important;
    }
    .userRankImage > img {
        /* Obrazek rangi */
        padding-bottom: 10px;
    }
    
    /* Tabela koło postu */
    dl.dataList dt {
        color: #353535;
    }
    
    /* Wywalenie obrzydliwego gradientu u GMów */
    .messageSidebar {
       background: none !important;
    }
    
    /* Zakładaki w profilu */
    .ui-tabs-nav {
        z-index: 1;
    }
    .tabMenu > ul > li.ui-state-active > a {
        margin: 0px !important;
    }
    .tabMenu > ul > li.ui-state-active:first-child {
        margin-left: 6px;
    } 
    .tabMenu > ul > li {
    margin: 0 3px -2px 2px;
    }
    
    /* Usunięcie podkreślenia do linkowania posta */
    .message .messageHeader .messageQuickOptions > li > .badge {
        text-decoration: none;
    }
    .message .messageContent .messageHeader .messageHeadline > p > a {
        text-decoration: none;
    }
}
