@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"), domain("desencyclopedie.wikia.com") {

/* ==== WIKIPEDIA vs. dEsenclopédie... a funny thing v.1 ==== */

/* WIKIPEDIA to dEsenclopédie - ALL -  ===

WIKIPEDIA FR:
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png");

=== */
#p-logo a {
    background-position: center center;
    background-repeat: no-repeat;
    display: block;
    height: 160px;
    text-decoration: none;
    width: 10em;
}
/* ORIGINAl */
/* .mw-wiki-logo {
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png");
} */

/* FUN */
.mw-wiki-logo {
    background-image: url("http://images4.wikia.nocookie.net/__cb20101109222714/desencyclopedie/images/2/2a/Logo_desencyclopedie_cauchy.png") !important;
}

#p-logo.portlet a[title="Page d'accueil"] ,
#p-logo.portlet a ,
#p-logo.portlet a:hover {
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png") !important;
}


#mw-content-text>p>span a[href="http://desencyclopedie.wikia.com/wiki/Portail:D%C3%A9ktionnaire"]{
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png") !important;
    background-color: red !important;
    z-index: 500 !important;
    display: inline-block !important;
}

/* AUTRES PAGES ===
- DEktionnaire -
http://desencyclopedie.wikia.com/wiki/D%C3%A9ktionnaire
- BEST OF -
http://desencyclopedie.wikia.com/wiki/Peuple_rom

- PB pour BEST OF au Hazard - 
http://desencyclopedie.wikia.com/wiki/Scatophile


==== */
#mw-content-text > p > span#nologo + span a img {
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png") !important;
background-color: yellow!important;
z-index: 500 !important;
opacity: 0 !important;
}
#mw-content-text > p > span#nologo + span a img:before {
content: "X" !important;
    background-image: url("https://fr.wikipedia.org/static/images/project-logos/frwiki.png") !important;
    background-color: yellow!important;
    z-index: 500 !important;
    opacity: 1 !important;
}


/* PB pour BEST OF au Hazard - 
http://desencyclopedie.wikia.com/wiki/Scatophile
=== */
#mw-content-text>div>span a img {
width: 130px !important;
height: 130px !important;
