@-moz-document domain("inoreader.com") {

/* Overall Background color */
#tree_pane, #subscriptions_fieldset, #subscriptions_buttons, #sb_rp_tools, #sb_tp_search_overlay, #reader_pane.reader_pane_view_style_4, #reader_pane.reader_pane_view_style_4 .unsubscribed_feed_suggestions_label {background: /*[[backgroundcolor]]*/;}

/* Read articles */
.ar.article_subscribed, div.article_current, div.article_current_collapsed, div.article_magazine.article_current {background: /*[[read]]*/;}

/* Unread articles */
.ar.article_unreaded, .article_unreaded .arrow_div, .article_tile.article_unreaded.article_expanded {background: /*[[unread]]*/}

/* pop up article thing */
#article_dialog, .article_full_contents, .article_footer {background: /*[[backgroundcolor]]*/;}

/* Links, highlights, little bits etc */
.unread_cnt {color: pink; font-size: 16px;}
A.bluelink:link {color: /*[[linkcolor]]*/;}

/* Additional overall black background color */
#no_more_div, #article_dialog, .article_full_contents, .article_footer, .article_expanded .article_footer_placeholder_middle.footerized.article_footer_placeholder_middle_sticky, #article_dialog .article_footer_placeholder_top, .article .arrow_div, #reader_pane.reader_pane_view_style_0 .article_expanded:hover, #reader_pane.reader_pane_view_style_0 div.article_expanded:hover div.arrow_div, #reader_pane.reader_pane_view_style_0 .article_expanded:hover, #reader_pane.reader_pane_view_style_0 div.article_expanded:hover div.popularity_div, #sb_tree_part, #reader_pane, #three_way_contents {background-color: /*[[backgroundcolor]]*/;}

}
