css:
@-moz-document domain("twitter.com") {

        .tweet-text-rtl {
        font-family: tahoma;
        font-size: 0.87em;
        -webkit-font-smoothing: antialiased;
        line-height: 1.5;
        }

        .dashboard .tweet-content .tweet-box[dir="rtl"] {
        font-family: tahoma;
        font-size: 0.9em;
        -webkit-font-smoothing: antialiased;
        line-height: 1.5;
        }

        .avatar, .DashboardProfileCard-avatarLink, .DashboardProfileCard-avatarImage, .conversation-module .conversation-tweet-item .stream-item-header .avatar, .ProfileAvatar, .ProfileAvatar-image, .ProfileAvatar-placeholderImage, .ProfileCardMini-avatarImage, .ProfileTweet-avatar {
        border-radius: 100px;
        }

        .ProfileCanopy-avatar {
        left: 40px;
        bottom: -107px;
        transition: all 0.3s ease-in-out;
        }

        .ProfileAvatar {
        width: 150px;
        height: 150px;
        transition: all 0.3s ease-in-out;
        }

        .ProfileAvatar:hover {
        transform: translateY(-10px)
        }

        .DashboardProfileCard {
        border: none;
        }

        .DashboardProfileCard-bg {
        border-bottom: none;
        border-radius: 5px;
        }

        .DashboardProfileCard {
        background: transparent;
        }

        .tweet-user {
        border-radius: 5px;
        }

        .module .flex-module {
        background-color: rgba(0, 0, 0, 0);
        border: medium none;
        line-height: 16px;
        padding: 15px;
        }

        .DashboardProfileCard-stats {
        border-top: 1px solid #E1EBF2;
        border-bottom: 1px solid #E1EBF2;
        margin: 4em 0 15px;
        }

        .trends {
        border-top: 1px solid #E1EBF2;
        border-bottom: 1px solid #E1EBF2;
        margin: 20px 0;
        opacity: 0.7;
        transition: all 0.3s ease-in-out;
        }

        .trends:hover {
        opacity: 1;
        }

        .follower-request-link {
        margin-left: 15px;
        opacity: 0.7;
        transition: all 0.3s ease-in-out;
        }

        .follower-request-link:hover {
        opacity: 1;
        }

        .DashboardProfileCard-content {
        margin-top: 0;
        opacity: 1;
        }

        .DashboardProfileCard-userFields {
        position: relative;
        margin-top: 2.2em;
        text-align: center;
        left: 45px;
        top: 0;
        }

        .DashboardProfileCard-avatarLink {
        background-color: #FFFFFF;
        margin: -3em 0 0 8px;
        padding: 3px 3px 1px 3px;
        vertical-align: bottom;
        position: relative;
        left: 6.4em;
        transition: all 0.3s ease-in-out;
        }

        .DashboardProfileCard-avatarLink:hover {
        transform: translateY(-5px)
        }

        .home-tweet-box {
        background: rgba(255, 255, 255, 0.7) !important;
        border: none;
        }

        .dashboard-left {
        transition: all 0.3s ease-in-out;
        }

        .ProfileHeaderCard {
        margin-top: 60px;
        }

        .ProfileTweet-text[dir="rtl"] {
        font-family: 'iran';
        line-height: 1.6;
        font-size: 1.2em;
        padding: 10px 0;
        }

        .activity-truncated-tweet {
        font-family: 'iran';
        line-height: 1.5;
        }

        .latest-tweet-timestamp {
        font-family: Helvetica;
        }

        .activity-truncated-tweet .tweet-text {
        font-size: 0.9em;
        }

        .permalink-tweet p.tweet-text-rtl {
        font-family: 'iran';
        font-size: 1.6em;
        }

        .stream-item, .unfocusable-stream-item {
        border-left: 1px solid #F4F4F4;
        border-right: 1px solid #F4F4F4;
        }

        .tweet {
        border-bottom: 1px solid #F4F4F4;
        }

        .content-header .header-inner, .content-no-header .no-header-inner {
        border-bottom: 1px solid #f4f4f4;
        }

        .content-header, .content-no-header {
        border-color: #f4f4f4
        }


        /* EDITS */

        .Trends .context-trend-item .trend-item-stats, .ProfileCardStats-statLabel, .MomentGuideNavigation-target, .MomentsGuidePage a:hover, .MomentsGuidePage a:focus, .MomentsGuidePage a:active, #global-actions > li:hover > a, #global-actions > li > a:focus, .nav.right-actions > li > a:hover, .nav.right-actions > li > button:hover, .nav.right-actions > li > a:focus, .nav.right-actions > li > button:focus, .MomentCapsuleSubtitle {
        color: #ca77a7;
        }

        .DMConversation-composer {
                border-top: 1px solid #ca77a7;
        }

        body {
        color: #b56eb7;
        }

        .AdaptiveSearchPage {
                background: #f5cbf6 !important;
        }

        a, .new-tweets-bar, .ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo, .icon-btn, .new-tweets-bar, .username, .time, .time a, .metadata, .metadata button.btn-link, .metadata a, .ProfileCardStats-statValue, .nav>li, .MomentCapsuleSummary-title, .MomentCapsuleSummary .MomentCapsuleLikesCount, .MomentCapsuleCover-title, .MomentUserByline-username, .ProfileCard a, .ProfileCard a:hover, .ProfileCard a:focus, .ProfileCard a:active, .ProfileCard .pretty-link, .ProfileCard .pretty-link:hover, .ProfileCard .pretty-link:focus, .ProfileCard .pretty-link:active, .ProfileCard-userFields, .top-timeline-tweetbox .timeline-tweet-box .tweet-form.condensed .tweet-box, .btn-link {
                color: #b56eb7;
        }

        .u-textUserColor, .u-textUserColorHover:hover, .u-textUserColorHover:hover .ProfileTweet-actionCount, .u-textUserColorHover:focus, .DMActivity-header, .DMActivity-title, .btn.small, .DMInboxItem-timestamp {
                color: #b56eb7 !important;
        }

        .u-borderUserColor, .u-borderUserColorHover:hover, .u-borderUserColorHover:focus, .u-borderUserColorLighter, .RichEditor {
                border-color: #b56eb7 !important;
        }

        .DirectMessage--sent .Caret:before {
                border-color: #b56eb7;
        }
        .new-tweets-bar, .tweet:hover, .DashboardProfileCard-content, .wtf-module.has-content, .TwitterCard-container--clickable:hover, .conversation-module .conversation-tweet-item .tweet:hover, .MomentCapsuleItemTweet-byline, .MomentCapsuleItemTweet-date a, .MomentCapsuleItemTweet-date a:hover, .MomentCapsuleItemTweet--withText  {
        background: #fdf7fd;
        }
        .DMInboxItem:hover {
                background: #fdf7fd;
                background-color: #fdf7fd !important;
        }
        .u-bgUserColorLightest {
                background: #fdf7fd !important;
        }
        .new-tweets-bar:hover, .DirectMessage--sent .DirectMessage-message {
                background: #b56eb7;
                color: #fff;
        }

        .MomentCapsuleItemTweet--withText .TweetTextSize {
                color: #292F33;
        }

        #global-actions > li > a {
                border-bottom-color: #b56eb7;
        }

        .global-nav .people .count .count-inner, .tweet-btn, .tweet-btn:focus, .tweet-btn.btn.disabled, .tweet-btn.btn.disabled:hover, .tweet-btn.btn[disabled], .tweet-btn.btn[aria-disabled=true] {
                background: #b56eb7;
        }

        .u-bgUserColor, .u-bgUserColorHover:hover, .u-bgUserColorHover:focus {
                background: #b56eb7 !important;
        }

        .trends {
                opacity: 1;
        }

        .TwitterCardsGrid {
                background: #fff;
        }
        .Footer-item, .Footer-link {
                color: #fff;
        }

        body,[dir="ltr"].logged-in {
                background-color: #f5cbf6;
            background-position: center !important;
            background-image: url(https://pbs.twimg.com/media/C3-uE9VW8AA61My.jpg) !important;
            background-size: cover !important;
            background-attachment: fixed !important;
        }
        body, .ProfilePage .AppContent {
                background-color: #f5cbf6;
            background-position: center !important;
            background-image: url(https://pbs.twimg.com/media/C3-uE9VW8AA61My.jpg) !important;
            background-size: cover !important;
            background-attachment: fixed !important;
        }
        body, .AdaptiveSearchPage .AppContent {
            background-color: #f5cbf6;
            background-position: center !important;
            background-image: url(https://pbs.twimg.com/media/C3-uE9VW8AA61My.jpg) !important;
            background-size: cover !important;
            background-attachment: fixed !important;
        }
}
