css:
@-moz-document domain("duckduckgo.com"){

    .tag-home__wrapper {
        display: none;
    }
    .search,
    .search--adv {
        background-color: rgb(222, 88, 51);
    }
    #pg-index {
        background-color: black !important;
    }
    .site-wrapper {
        background-color: black !important;
    }
    #header_wrapper {
        background-color: black !important;
        margin-bottom: 50px;
    }
    .header.cw,
    .header.cw--c {
        position: fixed;
        padding-left: 50%;
    }
    .feedback-btn {
        display: none;
    }
    .search--header {
        background-color: rgb(164, 56, 26);
        ;
        color: white;
    }
    .search__input,
    .search__input--adv {
        color: #ffffff;
        font-weight: bold;
    }
    #side_wrapper {
        margin-top: 10%;
        margin-right: 0 !important;
        left: 90%;
    }
    .search,
    .search--adv {
        border: 1px solid #666;
    }
    .header__logo-wrap {
        left: -51%;
        /* right: 1% !important; */
    }
    .header__search {
        left: -70%;
    }
    .header--aside {
        display: none;
    }
    #zero_click_wrapper {
        width: 50%;
        background-color: transparent;
    }
    .zci {
        background-color: hsl(11, 74%, 15%);
        background-color: hsl(11, 100%, 9%);
    }
    .zci__header {
        color: #c9481c;
    }
    .tile__foot--news {
        background-color: transparent;
    }
    .set-header--fixed .tileview--grid .metabar--fixed,
    .tileview--grid .metabar--fixed.is-stuck {
        background-color: rgba(98, 20, 3, 0.95);
    }
    #zci-images {
        margin-top: 12%;
        width: 200% !important;
    }
    .zci.is-active {
        border-bottom: 1px solid #de5833;
    }
    .metabar__in,
    .zcm-wrap,
    .zci__main,
    .zci__detail,
    .results-wrapper,
    .results--sidebar--mid {
        padding-left: 3%;
    }
    .tileview .metabar--fixed {
        background-color: rgba(222, 88, 51, 0.3);
        color: #fafafa;
    }
    .result__a {
        color: #992908;
    }
    .result__snippet {
        color: rgba(158, 158, 158, 0.9);
    }
    .result.highlight {
        background-color: #260b00;
    }
    .highlight .result__a {
        color: #fa8853;
    }
    .highlight .result__a:hover {
        color: rgba(205, 3, 3, 0.99);
    }
    .no-touch .zcm__link:hover {
        color: #8b0000;
    }
    map {
        background-color: rgb(7, 0, 18);
    }
    .tile__title {
        color: #de5833;
    }
    .tile__content--sm {
        color: red;
    }
    .tile__content--sm {
        color: #3a7fb4;
    }
    .acp-wrap {
        background-color: rgba(69, 17, 5, 0.84);
    }
    .acp,
    .acp--bang {
        color: rgb(246, 223, 190);
    }
    .acp--highlight {
        background-color: rgb(32, 7, 0);
    }
    .tile,
    .tile--s,
    .tile--info,
    .tile--map {
        background-color: rgba(0, 2, 51, 0.65);
    }
    .acp strong,
    .acp--bang strong {
        color: rgb(238, 181, 97);
    }
    .tile-nav.can-scroll:hover,
    .tile-nav.can-scroll:hover:after {
        background-color: #751414;
    }
    .tile-nav.can-scroll,
    .tile-nav.can-scroll:after {
        background-color: rgba(164, 42, 8, 0.75);
    }
    .metabar__grid-btn,
    .metabar__grid-btn:hover {
        color: #d1582f;
        background-color: rgba(14, 4, 67, 0.87);
    }
    .module__title__link,
    .module__title__link:hover,
    .module__title__link:active,
    .module__title__link:visited {
        color: #b0491a;
    }
    .about-info-box__info-label {
        color: #873c08;
    }
    .c-info__title,
    .c-icon__title,
    .c-list__title,
    .c-base__title {
        color: #b23d1d;
    }
    .text--primary,
    .tx-clr--dk,
    .tx-clr--slate {
        color: #b23d1d;
    }
    .result__a:visited {
        color: rgba(201, 206, 244, 0.99);
    }
    .has-code h1,
    .has-code h2,
    .has-code h3,
    .has-code h4,
    .has-code h5,
    .has-code h6 {
        color: #b23d1d;
    }
    #links_wrapper {
        padding-left: 5%;
    }
    body,
    input,
    select,
    textarea {
        color: #a4381a;
    }
    .cw,
    .cw--c {
        max-width: 800px;
    }
	.badge-link {
		display:none;
		visibility: hidden;
    }

.search-filters-wrap:before, .search-filters-wrap:after {
    background-image: none !important;
}
.results--sidebar {
    position: fixed;
}
.header.cw, .header.cw--c {
    margin-left: 50%;
    padding-left: 0;
}
}
