css:
@-moz-document domain("www.gamezer.com") {
.DashIcon {
    width: 100px;
    height: 100px;
    background-size: 400px 500px;
    box-shadow: rgba(0, 0, 0, 0.5) 0px 5px 15px;
    background-image: url(http://s01.arab.sh/i/00100/fs1qsesagwr5.png);
    margin: 5px auto 20px;
    border-radius: 100%;
    background-repeat: no-repeat;
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
#Block_FHeader {
    height: 40px;
    padding: 5px;
    display: none;
    z-index: 10;
    border-radius: 5px 5px 0 0;
    overflow: hidden;
    background: none;
    background-color: rgba(76, 54, 144, 0.53);
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.59);
}
.GameIcon {
    width: 35px;
    height: 35px;
    content: "bg#dashicons";
    background-size: 35px 35px;
    background-image: url("http://s01.arab.sh/i/00100/yewgxrwetv7w.png");
    background-repeat: no-repeat;
}
.GameItem {
    width: 180px;
    height: 100px;
    vertical-align: top;
    position: relative;
    z-index: 1;
    margin: 37px 3px 10px 3px;
    padding-bottom: 5px;
    display: inline-block;
 background-size: 119px 1px;
    background-image: url("http://s02.arab.sh/i/00100/ym6vixaz3oaa.png");
    background-repeat: no-repeat;
    background-position: 50% 70%;
    text-align: center;
    background-color: rgba(33, 33, 33, 0.45);
    border: 1px solid #666;
    border-radius: 10px;
    box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
}
.GameItemTitle {
    background-color: rgba(22, 22, 22, 0.15);
    color: #999;
    font-weight: bold;
    font-size: 15px;
    padding: 20px 3px 0;
    margin-bottom: 4px;
}
.GameItemIcon {
    width: 35px;
    height: 35px;
    background-image: url("http://s01.arab.sh/i/00100/yewgxrwetv7w.png");
    position: absolute;
    z-index: 2;
    top: 0px;
    left: 100%;
    margin-left: -35px;
    border-radius: 100%;
    overflow: hidden;
    -webkit-transform: translate3d(0, 0, 0);
    background-color: rgba(144, 139, 173, 0);
    box-shadow: 0px 4px 16px rgba(0, 0, 0, 0);
    -moz-box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.5);
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
.GameItemStart {
    width: 180px;
    height: 100px;
    vertical-align: top;
    position: relative;
    z-index: 1;
    margin: 37px 3px 10px 3px;
    padding-bottom: 5px;
    display: inline-block;
    text-align: center;
    color: #c9c9c9;
    font-size: 17px;
    font-weight: bold;
    line-height: 98px;
    background-size: 73px 68px;
    background-image: url("http://s01.arab.sh/i/00100/ujlfmdf627j0.png");
    background-repeat: no-repeat;
    background-position: center;
    background-color: rgba(38, 36, 66, 0.5);
    border: 1px solid #666;
    border-radius: 10px;
    box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 5px 18px rgba(0, 0, 0, 0.4);
}
.InvitationContainer {
    width: 100%;
    height: 100%;
    position: relative;
    overflow-y: auto;
    overflow-x: hidden;
    border-radius: 10px;
    background-color: rgba(47, 38, 72, 0.9);
    border: 1px solid #59409e;
    box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -moz-box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -webkit-box-shadow: 0px 4px 18px rgba(66, 42, 130, 0.8);
}
.InvitationHeaderLine {
    width: 100%;
    height: 31px;
    line-height: 31px;
    background-color: rgba(117, 98, 255, 0.1);
    color: #fff;
    font-size: 14px;
    font-weight: bold;
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
.PrivateMessage {
    width: 40px;
    height: 40px;
    margin-top: -20px;
    margin-left: -20px;
    position: absolute;
    border-radius: 50%;
    opacity: 0;
    overflow: hidden;
    background-color: rgba(43, 27, 86, 0.85);
    border: 1px solid #38246d;
    box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -moz-box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -webkit-box-shadow: 0px 4px 18px rgb(66, 42, 130);
}
.AbsentUserInfo {
    width: 300px;
    height: 146px;
    margin-top: -71px;
    margin-left: -150px;
    position: fixed;
    top: 50%;
    left: 50%;
    border: 1px solid #38246d;
    border-radius: 10px;
    opacity: 0;
    overflow: hidden;
    background-color: rgba(43, 27, 86, 0.9);
    box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -moz-box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.65);
    -webkit-box-shadow: 0px 4px 18px rgb(66, 42, 130);
}
#RoomPMInput,
#RoomVMInput {
    width: 100%;
    height: 45px;
    color: #fffde7;
    font-size: 16px;
    font-family: Arial, Verdana, sans-serif;
    padding: 0 29px 0 5px;
    display: block;
    resize: none;
    border: none;
    background-color: rgba(44, 27, 91, 0.15);
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
#alert {
    width: 300px;
    margin: -40px 0 0 -150px;
    left: 50%;
    top: 50%;
    position: fixed;
    z-index: 10000;
    text-align: center;
    color: #ffffff;
    font-size: 15px;
    vertical-align: middle;
    padding: 10px 10px 15px 10px;
    background-color: rgba(52, 38, 113, 0.90);
    border-radius: 10px;
    border: 1.5px solid #584ca0;
    box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.3);
    -webkit-box-shadow: 0px 5px 15px rgb(60, 45, 128);
}
.leftBlock {
    border-radius: 6px;
    background-color: rgb(83, 71, 125);
}
.UserItem {
    height: 80px;
    position: relative;
    background-image: url("http://s01.arab.sh/i/00100/90y1wesvrimj.png");
    background-repeat: repeat;
    background-color: #200f5f;
    border-radius: 5px;
    margin-bottom: 2px;
    white-space: nowrap;
    overflow: hidden;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.3);
    -webkit-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.3);
}
.GameItemName {
    margin: 5px;
    overflow: hidden;
    line-height: 20px;
    color: #fff;
    font-size: 15px;
    font-weight: bold;
    font-family: Arial,Verdana,sans-serif;
    white-space: nowrap;
}

/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
.UserAvatarContainer {
    width: 76px;
    height: 76px;
    overflow: hidden;
    position: absolute;
    z-index: 1;
    left: 2px;
    top: 2px;
    border-radius: 5px;
}
.desktop .list_scroller::-webkit-scrollbar {
    width: 12px;
    background-color: #4644bf;
}
.desktop .list_scroller::-webkit-scrollbar-thumb {
    background-color: #32275e;
}
.UserInfoContainer {
    width: 100%;
    height: 100%;
    position: relative;
    overflow-y: auto;
    -webkit-transform: translate3d(0, 0, 0);
    overflow-x: hidden;
    border-radius: 10px;
    background-color: #23144f;
    border: 1px solid #634ba4;
    box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.5);
    -moz-box-shadow: 0px 4px 18px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 0px 4px 18px rgb(78, 53, 146);
}
.InvitationTimer #svg_background {
    stroke: rgba(33, 33, 33, 0.75);
    stroke-width: 6px;
}
.InvitationTimer #svg_bar {
    stroke: rgba(222, 215, 215, 0.4);
    stroke-width: 6px;
}
.UserBG {
    border-radius: 5px;
    background-color: #241550;
    opacity: 0.6 !important;
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
.StoreItemBlock {
    width: 100%;
    min-height: 84px;
    padding: 10px 0 10px 10px;
    background-color: rgba(169, 169, 169, 0.2);
    overflow: hidden;
    border-radius: 6px;
    font-size: 16px;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
}
.StoreSummary {
    color: #67b17e;
    font-size: 16px;
    font-weight: bold;
}
.StoreOrderNum {
    color: #dcdcdc;
    font-size: 19px;
    line-height: 19px;
    font-weight: bold;
    margin: 8px 0 5px 0;
}
.order_warning {
    font-weight: normal;
    color: #daa458;
    display: inline-block;
}
.StoreOrder .StoreItemBlock {
    color: #b3b3b3;
    font-size: 15px;
}
.StoreItem.Option .StoreItemBlock {
    color: #b3b3b3;
    font-size: 15px;
    line-height: 17px;
    padding: 11px 5px 8px 140px;
}
.StoreItemBlockCompact {
    width: 100%;
    min-height: 20px;
    padding: 10px;
    background-color: rgba(169, 169, 169, 0.2);
    overflow: hidden;
    border-radius: 6px;
    color: #b3b3b3;
    font-size: 17px;
    font-size: 16px;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.4);
}
.StatUser {
    position: relative;
    overflow: hidden;
    white-space: nowrap;
    padding: 5px 50px 5px 44px;
    margin-top: 2px;
    border-radius: 5px;
    background-color: #eae7f5;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.4);
}
#Dash_Section.help {
    background-image: none;
    background-color: #eae7f5;
}
/* Desgined By : ˏ κʌʟʌѕɴɨкσv ´ */
}
