css:
@-moz-document url("https://thepiratebay.org/") {
body {
    background-color: #1e1e2a;
}
head {
    visibility: hidden;
}
#fp #navlinks {
    visibility: hidden;
}
label {
    visibility: hidden;
}
p#subm {
    visibility: hidden;
}
a {
    visibility: hidden;
}
footer {
    visibility: hidden;
}
#fp #inp input {
    padding: 10px;
    width: 1200px;
    /* border-color: #0a31c1; */
    border-width: 0px;
    margin-top: -500px;
    background-color: #16161e;
    -moz-box-shadow:    1px 1px 2px 2px #ccc;
    -webkit-box-shadow: 1px 1px 3px 3px #ccc;
    box-shadow:         0.5px 0.5px 3px 3px #191926;
    text-align: center;
    color: white;
}
}

@-moz-document url-prefix("https://thepiratebay.org/search/"), url-prefix("https://thepiratebay.org/browse/") {
body {
    background-color: #1e1e2a;
}
div#header {
    color: transparent;
}
img {
    visibility: hidden;
}
label {
    visibility: hidden;
}
div#header a {
    visibility: hidden;
}
select {
    visibility: hidden;
}
input.submitbutton {
	visibility: hidden;
}
#q .inputbox {
    margin-left: -40px;
    margin-top: 10px;
    margin-bottom: -50px;
    width: 900px;
    vertical-align: middle;
    padding: 10px;
    border-width: 0px;
    background-color: #16161e;
    box-shadow: 0.5px 0.5px 3px 3px #191926;
    text-align: center;
    color: white;
}
h2 {
    visibility: hidden;
}
table#searchResult tr {
    background: #F6F1EE;
    color: #fff;
}
thead#tableHead a {
    color: #fff;
    font-weight: bold;
}
table#searchResult th {
    padding: 10px;
    box-shadow: 0.5px 0.5px 0.5px 0.5px #191926;
    border: 1px transparent;
    color:white;
}
table#searchResult td {
    padding: 10px;
    box-shadow: 0.5px 0.5px 0.5px 0.5px #191926;
    border: 1px transparent;
    color:black;
}
.detDesc {
    color: #909596;
    margin-left: -50px;
}
table#searchResult {
    font-size: 1em;
    /* border-collapse: collapse; */
    border-spacing: 1px;
    margin: 0;
    text-align: left;
    width: 100%;
    min-width: 400px;
    box-shadow: 0.5px 0.5px 0.5px 0.5px #191926;
}
table#searchResult a {
    border: 0;
    margin-left: -0px;
}
thead#tableHead th {
    background: #323248;
        box-shadow: 0.5px 0.5px 0.5px 0.5px #191926;

}
a {
    color: #d0d0d0;
    text-decoration: none;
    border-bottom: 1px dotted #000;
}
table#searchResult tr {
    background: #515167;
    color: #fff;
}
table#searchResult tr:hover {
    background: #63637d;
    color: #fff;
}
table#searchResult th:hover {
    background: #63637d;
}
table#searchResult tr.alt {
    background: #515167;
}
table#searchResult td:hover {
    background: #63637d;
    color: #fff;
}
table#searchResult td {
    background: #515167;
    color: #fff;
}
table#searchResult td.vertTh {
    font-weight: bold;
    color: #d0d0d0;
}
.img::after {
    content: url('http://i.imgur.com/ghhqGnN.png');
    padding-right: 40px;
    visibility: visible;
    position: relative;
    right: -520px;
    top: 0px;
}
#q {
    display: -webkit-inline-box;
    text-align: left;
    margin: 0;
    width: 860px;
}
div br {
    display: none;
}
#TPBlogo {
    float: inherit;
    border: 0px;
    padding: 0px;
    display: -webkit-inline-box;
    margin: -115px;
}
}

