css:
@-moz-document url-prefix("https://www.synchronkartei.de") {
body {
	background-color : #000;
	color            : #FFF;
}
a {
	color : #00D0D0;
}
a:hover, a:focus {
	color : #00FFFF;
}
a.sort-table-header {
	color : #00D0D0;
}
a.sort-table-header:hover {
	color : #00FFFF;
}
.form-control {
	background-color : #000;
	color            : #FFF;
}
.btn-default {
	background-color : #000;
	color            : #FFF;
}
.btn-default.disabled:hover , 
.btn-default[disabled]:hover , 
fieldset[disabled] .btn-default:hover , 
.btn-default.disabled:focus , 
.btn-default[disabled]:focus , 
fieldset[disabled] .btn-default:focus , 
.btn-default.disabled.focus , 
.btn-default[disabled].focus , 
fieldset[disabled] .btn-default.focus {
	background-color : #000;
	border-color     : #CCC;
}
.breadcrumb {
	background-color : #333;
	color            : #DDD;
}
.breadcrumb>.active {
	color : #FFF;
}
.panel {
	background         : #333;
	-webkit-box-shadow : 0 0 0 transparent;
	box-shadow         : 0 0 0 transparent;
}
.btn-primary {
	color            : #FFF;
	background-color : #01A7A7;
}
.progress-bar {
	color     : #333;
	font-size : 14px;
}
.alert-info {
	background-color : #465259;
	border-color     : #666;
	color            : #4daede;
}
.alert-warning {
	background-color : #4C4310;
	color            : #DECAA9;
}
/* advertisment */
div.ad-ho {
	background-color : #333;
	border-color     : #00D0D0;
}
.dropdown-menu {
	background-color : #333;
	border           : 1px solid #666;
	color            : #FFF;
}
.dropdown-menu > li > a:hover , 
.dropdown-menu > li > a:focus {
	background-color : #444;
	color            : #FFF;
}
.dropdown-menu > li>a {
	color : #FFF;
}
/* dropdown menu */
.btn-default:hover {
	background-color : #333;
	color            : #FFF;
}
.btn-default:active , .btn-default.active , .open > .dropdown-toggle.btn-default {
	background-color : #333;
	color            : #FFF;
}
.navbar-form .btn[type="submit"]:hover {
	background-color : #000;
	color            : #00D0D0;
}
code {
	background-color : #333;
	color            : #FFF;
}
.dropdown-menu .divider {
	background-color : #666;
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/serie/") {
.nav-tabs > li.active > a , 
.nav-tabs > li.active > a:hover , 
.nav-tabs > li.active > a:focus {
	background-color : #000;
	color            : #FFF;
}
/*
kleine korrektur für die reiter
um die kleinen unterbrechungen in der unteren rahmen farbe zu unterbinden
TODO muss in anderen browsern getestet werden
deaktivierte episodenliste - https://www.synchronkartei.de/serie/32854 [Achterbahn der Gefühle (1997)]
aktive episodenliste - https://www.synchronkartei.de/serie/236 [Das A-Team (1983-1987)]
*/
.nav-tabs > li > a {
	border-left-width  : 0px;
	border-right-width : 0px;
}
.nav-tabs > li > a:hover {
	border-color : #333 #333 #ddd;
}
.nav > li > a:hover , 
.nav > li > a:focus ,
.nav-tabs > li.disabled > a:hover ,
.nav-tabs > li.disabled > a:focus {
    background-color: #333;
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/sprecher/") {
/* urls beginnend - https://www.synchronkartei.de/sprecher/ */
.panel-default > .panel-heading {
	background-color : #333;
	color            : #FFF;
}

}

@-moz-document url("https://www.synchronkartei.de/statistiken"), url-prefix("https://www.synchronkartei.de/sprecher/"), url-prefix("https://www.synchronkartei.de/darsteller/"), url-prefix("https://www.synchronkartei.de/film/"), url-prefix("https://www.synchronkartei.de/serie/") {
.pagination > li > a , 
.pagination > li > span {
	background-color : #333;
	border-color     : #c0c0c0;
	color            : #FFF;
}
.pagination > .disabled > span , 
.pagination > .disabled > span:hover , 
.pagination > .disabled > span:focus , 
.pagination > .disabled > a , 
.pagination > .disabled > a:hover , 
.pagination > .disabled > a:focus {
	background-color : #000;
	color            : #C0C0C0;
}
.pagination > li > a:hover , 
.pagination > li > span:hover , 
.pagination > li > a:focus , 
.pagination > li > span:focus {
	background-color : #444;
	color            : #FFF;
}
.list-group-item {
	background-color : #333;
	border-color     : #666;
}
.list-group-item:nth-child(2n) {
	background-color : #222;
}
a.list-group-item:hover , 
button.list-group-item:hover , 
a.list-group-item:focus , 
button.list-group-item:focus {
	background-color : #444;  
}
a.list-group-item, button.list-group-item {
	color : #FFF;
}
a.list-group-item:hover ,
button.list-group-item:hover , 
a.list-group-item:focus , 
button.list-group-item:focus {
	color : #FFF;
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/wizard") {
/* Anlegen von Darsteller / Sprecher / Film | Serie */
.panel-default {
	border-color : #009292;
}
.panel-default>.panel-heading {
	color            : #00d0d0;
	background-color : #222;
	border-color     : #ccc;
}
.panel-default>.panel-heading:hover {
	background-color : #111;
}
.panel-heading a:hover {
	color : #303030;
}
.panel-heading a:hover {
	color : #00ffff;
}
.nav>li>a:hover , 
.nav>li>a:focus {
	background-color : #555;
}
.panel-default>.panel-heading+.panel-collapse>.panel-body {
	border-top-color : #009292;
}
.collapse.in {
	background-color : #222;
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/") {
/* korrekturen - global - oder - benötigt nacharbeitung */

/**
 * mit transparentem hintergrund versehene glühbirne - START
 */
img.hinweis {
	display : none;
}
a[title="Hinweis verfassen"] {
	background : url(data:image/png;base64,);
	background-repeat : no-repeat;
	display           : inline-block;
	height            : 19px;
	width             : 19px;
}
/**
 * mit transparentem hintergrund versehene glühbirne - END
 */

/**
 * tmdb logo - Powered by Rectangle (green) https://www.themoviedb.org/about/logos-attribution - START
 */
a[title="TMDb"] img {
	display : none;
}
a[title="TMDb"] {
	background-image : url(data:image/png;base64,);
	background-position : 0 0;
	background-repeat   : no-repeat;
	display             : inline-block;
	height              : 40px;
	width               : 101px;
}
/**
 * tmdb logo - Powered by Rectangle (green) https://www.themoviedb.org/about/logos-attribution - END
 */

/**
 * werbung - dunkler gemachen
 */
.ad-ho img ,
.ad-ho iframe {
	filter : grayscale(100%) invert(100%);
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/darsteller/"), url-prefix("https://www.synchronkartei.de/sprecher/") {
/* korrekturen - darsteller & sprecher */
a[title="Hinweis verfassen"] {
	background-position : 0px 3px;
}

}

@-moz-document url-prefix("https://www.synchronkartei.de/statistiken") {
/**
 * Build-Datum: 03.04.2017 23:14:27 CEST, Revision: c8c8994
 */
li:last-of-type:last-of-type a.list-group-item {
	border-color : #666;
}
/* korrektur
 * der übersichtlichkeits halber - jeweils ein kleiner abstand zwischen den einträgen 
 */
#statistiken ol > li {
	padding-bottom : 2px;
}
/* korrektur 
 * da die bilder 404 fehler verursachen und in google chrome somit mit platzhaltern versehen sind
 * werden diese sowie die platzhalter ausgeblendet
 * bei einer anpassung in der html ausgabe kann der nachfolgende css code getrost gelöscht werden
 */
img[src^="/pics"] ,
span.ovnopic {
	display : none;
}
}

@-moz-document url-prefix("https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/") {
/*
soundcloud - iframe
diesen teil auf synchronkartei.de am besten auslassen.
da es keine cross domain syntax in css gibt.
*/
html[stylus-iframe] body {
	color : #DDD;
}
html[stylus-iframe] .widget {
	border-color : #666;
}
html[stylus-iframe] .g-shadow-inset {
	box-shadow : inset 0 0 0 0 #666;
}
html[stylus-iframe] .g-background-default {
	background-color : #000;
}
html[stylus-iframe] .g-text-shadow {
	text-shadow : 0 1px 0 #000;
}
html[stylus-iframe] a.sc-link-medium {
	color : #DDD;
}
html[stylus-iframe] a.sc-link-medium:hover {
	color : #FFF;
}
html[stylus-iframe] a.sc-link-dark {
	color : #DDD;
}
html[stylus-iframe] a.sc-link-dark:hover , 
html[stylus-iframe] a.sc-link-medium:hover {
	color : #FFF;
}
html[stylus-iframe] .waveform__layer .sceneLayer:last-of-type {
	filter : invert(100%);
}
html[stylus-iframe] .commentPopover.darkText .commentPopover__body {
	color: #AAA;
}
html[stylus-iframe] .teaser {
	background : #333;
}
html[stylus-iframe] .teaser__trackLink:active , 
html[stylus-iframe] .teaser__trackLink:focus , 
html[stylus-iframe] .teaser__trackLink:hover {
	color: #FFF;
}
html[stylus-iframe] .teaser__trackLink, 
html[stylus-iframe] .teaser__trackLink:visited {
	color: #EEE;
}
html[stylus-iframe] .sound__panel {
	background: #000;
}
html[stylus-iframe] .sc-background-light {
	background-color: #666;
}

}
