/* ----------------------------------------------------------------------------------------------------------- */
/* 本地文件主题 设定*/ 
@namespace url(http://www.w3.org/1999/xhtml);
/* ----------------------------------------------------- */
@-moz-document url-prefix("file:///")
{  
	body {
		/*overflow: auto !important;/*overflow-y*/
		/*background:  none/*100% 100% !important;/**/
		background-color:  #93B468 !important;/*#93B468*//*rgb(147,180,104)*//*#648637*/
		/*transform: rotate(90deg);/**/
	}
	/*html * {
		 overflow-y: auto !important;
	}/**/

/* 本地网页html文件 */
*  { 
		  /*white-space: pre-wrap !important ; /* 自动换行 */
		  word-wrap:  break-word !important;
		  max-width: 100% !important ; /* 不超宽 */
	}
body > *
     {   max-width: 100% !important;/*none*/ }
	 
/* 保持 本地html文件 网页链接 字体 颜色不变 避免晃眼 */
	a:visited,a:link,a:hover {
	    color: inherit !important;
		position: inherit !important;
		text-align:  inherit !important;
		text-decoration: none !important;/* 去掉下划线 */
	}

/* 本地web文件 去底层背景*/
	body[background="image/000.GIF"],
	table[background="image/012.PNG"]{
		background:  none !important;
		}/**/
/* 本地web文件 去广告*/
	img[src="image/xing1.gif"],	
	td[background="image/001.jpg"],
	/*td[background="image/011.jpg"],/**/
	/*td[background="image/013.jpg"],/**/
	img[src="image/021.png"],
	td[background="image/022.png"],
	img[src="image/023.png"],
	#bkk > table:nth-child(2),
	#bkk > table:nth-child(2) > tbody:nth-child(1){
		display:  none !important;
		}/**/
/* 本地web文件 增大字体*/
	div[id="txt"]/*, #txt/**/, #text/*, #txt > p:nth-child(*)/**/{
		font-size: 2.0em !important;
		width: 70% !important;/* 宽度收窄 */
		white-space: pre-wrap !important;/* 自动换行 */
		word-wrap: break-word !important;
		overflow-y: auto !important;/* 显示滚动条 *//*overflow-y*//*visible*/
		}
	div[class="content"]{
		font-size: 1.10em !important;
		}
/*本地web文件 Flash禁止*/
	embed[type="application/x-shockwave-flash"]{
		 display: none !important;
     }

	html >
    	body:not([bgcolor]) >
		      pre:only-child
			  {
     			background: #A2C672 !important;/*rgb(162,198,114)*//* #A2C672 */
				/*color: #222222 !important;/* bbb */
				/*overflow: auto !important;/**/
			  }
	
/* 本地纯文本文件 字体增大 加边框 */
	pre:only-child 
			  {
			  	background-attachment: fixed !important;
				background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAIAAAC0tAIdAAAAQklEQVQokWP88f0rAwMDAwMDOwfXzx/fGGAAK5cJzv/54xs7Bxd+LuP///8JGgnnMhFjJEI1kW5AqCZew6gvB7MvAQNCkqu0O3vtAAAAAElFTkSuQmCC");
				background-repeat: repeat /*!important/**/;
				/*background-color: #0B0B0B !important;/*#A2D8EE/**//*black/**//*rgb(162,216,238)/**//* 背景色 */
				color: #0A0A0A !important;/*#232323*//* 颜色 */
				border: 12px solid #606060 !important;/* 边框 *//*#A0A0A0*/
				border-radius: 24px 24px 24px 24px !important;/*-moz-border-radius/**/
				/*opacity: 1 !important;/**/
				/*font-color: #000 !important;/**/
				font-size: 1.35em !important; /**/
				/*text-shadow:1px 1px 7px #505050; /*字体阴影*/
				/*text-align: center !important;/* 字体中间对齐 */
				overflow-y: auto !important;/* 显示滚动条 *//*overflow-y*//*visible*/
				margin: auto !important;/* 居中 *//*margin-left*//*margin-right*/
				/*width: -moz-available !important;/* 最宽化 */
				width: 70% !important;/* 宽度收窄 */
				white-space: pre-wrap !important;/* 自动换行 */
				word-wrap: break-word !important;
	}
}
