css:
/* Normal, standard links. */

a:link {
    color: #0095D7;
    text-decoration: none;
}
a:visited {
    color: #0095D7;
    text-decoration: none;
}
a:hover {
    text-decoration: underline;
}
/* Navigation links - for the link tree. */

.nav,
.nav:link,
.nav:visited {
    color: #000000;
    text-decoration: none;
}
a.nav:hover {
    color: #cc3333;
    text-decoration: underline;
}
/* Navigation links - for the link tree. */

.nav,
.nav:link,
.nav:visited {
    color: #e60a19;
    text-decoration: none;
}
a.nav:hover {
    color: #cc3333;
    text-decoration: underline;
}
/* Tables should show empty cells. */

table {
    empty-cells: show;
}
/* By default (td, body..) use verdana in white. */

body,
td,
th,
tr {
    color: #eeeeee;
    font-size: 14px;
    font-family: tahoma, sans-serif;
}
/* The main body of the entire forum. */

body {
    background-color: #000000;
    margin: 0px;
    padding: 0px 0px 0px 0px;
    background-repeat: no-repeat;
    background-attachment: fixed;
}
/* Input boxes - just a bit smaller than normal so they align well. */

input,
textarea,
button {
    color: #000000;
    font-family: tahoma, sans-serif;
}
input,
button {
    font-size: 90%;
}
textarea {
    font-size: 100%;
    color: #000000;
    font-family: tahoma, sans-serif;
}
/* All input elements that are checkboxes or radio buttons. */

input.check {}
/* Selects are a bit smaller, because it makes them look even better 8). */

select {
    font-size: 90%;
    font-weight: normal;
    color: #777777;
    font-family: tahoma, sans-serif;
}
/* Standard horizontal rule.. ([hr], etc.) */

hr,
.hrcolor {
    height: 1px;
    border: 0;
    color: #000000;
    background-color: #000000;
}
/* No image should have a border when linked */

a img {
    border: 0;
}
/* A quote, perhaps from another post. */

.quote {
    color: #eeeeee;
    background-color: #666666;
    border: 1px solid #000000;
    margin: 1px;
    padding: 1px;
    font-size: small;
    line-height: 1.4em;
}
/* A code block - maybe even PHP ;). */

.code {
    color: #000000;
    background-color: #dddddd;
    font-family: "courier new", "times new roman", monospace;
    font-size: x-small;
    line-height: 1.3em;
    /* Put a nice border around it. */
    
    border: 1px solid #000000;
    margin: 1px auto 1px auto;
    padding: 1px;
    width: 99%;
    /* Don't wrap its contents, and show scrollbars. */
    
    white-space: nowrap;
    overflow: auto;
    /* Stop after about 24 lines, and just show a scrollbar. */
    
    max-height: 24em;
}
/* The "Quote:" and "Code:" header parts... */

.quoteheader,
.codeheader {
    color: #777777;
    text-decoration: none;
    font-style: normal;
    font-weight: bold;
    font-size: small;
    line-height: 1.2em;
}
/* Generally, those [?] icons.  This makes your cursor a help icon. */

.help {
    cursor: help;
}
/* /me uses this a lot. (emote, try typing /me in a post.) */

.meaction {
    color: red;
}
/* The main post box - this makes it as wide as possible. */

.editor {
    width: 96%;
}
/* Highlighted text - such as search results. */

.highlight {
    background-color: yellow;
    font-weight: bold;
    color: black;
}
/* Alternating backgrounds for posts, and several other sections of the forum. */

.windowbg {
    color: #fff;
    background-color: #252525;
}
.windowbg2 {
    color: #fff;
    background-color: #1D2E3E;
}
.windowbg3 {
    color: #fff;
    background-color: #252525;
}
/* the today container in calendar */

.calendar_today {
    background-color: #000000;
}
/* These are used primarily for titles, but also for headers (the row that says what everything in the table is.) */

.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td {
    color: white;
    font-style: normal;
    background: url #000000 repeat-x;
    border-bottom: solid 1px #000000;
    border-top: solid 1px #000000;
    padding-left: 10px;
    padding-right: 10px;
}
.titlebg,
.titlebg a:link,
.titlebg a:visited {
    font-weight: bold;
    color: white;
    font-style: normal;
}
.titlebg a:hover {
    color: #404040;
}
/* same as titlebg, but used where bold text is not needed */

.titlebg2 a:link,
.titlebg2 a:visited {
    color: white;
    font-style: normal;
    text-decoration: underline;
}
.titlebg2 a:hover {
    text-decoration: underline;
}
/* This is used for categories, page indexes, and several other areas in the forum.
.catbg and .catbg2 is for boardindex, while .catbg3 is for messageindex and display headers*/

.catbg,
tr.catbg td,
.catbg3,
tr.catbg3 td {
    background: url #000000 repeat-x;
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
}
.catbg2,
tr.catbg2 td {
    background: url #000000 repeat-x;
    color: #ffffff;
    padding-left: 10px;
    padding-right: 10px;
}
.catbg,
.catbg2,
.catbg3 {
    border-bottom: solid 1px #000000;
}
.catbg,
.catbg2 {
    font-weight: bold;
}
.catbg3,
tr.catbg3 td,
.catbg3 a:link,
.catbg3 a:visited {
    font-size: 95%;
    color: white;
    text-decoration: none;
}
.catbg a:link,
.catbg a:visited,
.catbg2 a:link,
.catbg2 a:visited {
    color: white;
    text-decoration: none;
}
.catbg a:hover,
.catbg2 a:hover,
.catbg3 a:hover {
    color: #e0e0ff;
}
/* This is used for tables that have a grid/border background color (such as the topic listing.) */

.bordercolor {
    background-color: #000000;
    padding: 0px;
}
/* This is used on tables that should just have a border around them. */

.tborder {
    padding: 1px;
    border: 1px solid #333333;
    background-color: #000000;
}
/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */

.smalltext {
    font-size: 12px;
    font-family: tahoma, sans-serif;
}
.middletext {
    font-size: 90%;
}
.normaltext {
    font-size: small;
}
.largetext {
    font-size: large;
}
/* Posts and personal messages displayed throughout the forum. */

.post,
.personalmessage {
    width: 100%;
    overflow: auto;
    line-height: 1.3em;
}
/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */

.signature {
    width: 100%;
    overflow: auto;
    padding-bottom: 3px;
    line-height: 1.3em;
}
/* Sometimes there will be an error when you post */

.error {
    color: red;
}
/* definitions for the main tab, active means the tab reflects which page is displayed */

.maintab_first,
.maintab_back,
.maintab_last,
.maintab_active_first,
.maintab_active_back,
.maintab_active_last {
    color: white;
    text-transform: lowercase;
    vertical-align: top;
}
.maintab_back,
.maintab_active_back {
    color: #e60a19;
    text-decoration: none;
    font-size: 15px;
    vertical-align: top;
    padding: 2px 6px 6px 6px;
    font-family: tahoma, sans-serif;
}
.maintab_first {
    background: url no-repeat;
    width: 29px;
    height: 27px;
}
.maintab_back {
    background: url repeat-x;
}
.maintab_last {
    background: url no-repeat;
    width: 29px;
    height: 27px;
}
.maintab_active_first {
    background: url no-repeat;
    width: 6px;
}
.maintab_active_back {
    background: url repeat-x;
}
.maintab_active_last {
    background: url no-repeat;
    width: 8px;
}
/* how links behave in main tab. */

.maintab_back a:link,
.maintab_back a:visited,
.maintab_active_back a:link,
.maintab_active_back a:visited {
    color: white;
    text-decoration: none;
}
.maintab_back a:hover,
.maintab_active_back a:hover {
    color: #e60a19;
    text-decoration: none;
}
/* definitions for the mirror tab */

.mirrortab_first,
.mirrortab_back,
.mirrortab_last,
.mirrortab_active_first,
.mirrortab_active_back,
.mirrortab_active_last {
    color: black;
    text-transform: lowercase;
    vertical-align: top;
}
.mirrortab_back,
.mirrortab_active_back {
    color: #e60a19;
    text-decoration: none;
    font-size: 9px;
    vertical-align: bottom;
    padding: 6px 6px 2px 6px;
    font-family: tahoma, sans-serif;
}
.mirrortab_first {
    background: url no-repeat;
    width: 29px;
    height: 27px;
}
.mirrortab_back {
    background: url repeat-x;
}
.mirrortab_last {
    background: url no-repeat;
    width: 29px;
    height: 27px;
}
.mirrortab_active_first {
    background: url no-repeat;
    width: 6px;
}
.mirrortab_active_back {
    background: url repeat-x;
}
.mirrortab_active_last {
    background: url no-repeat;
    width: 8px;
}
/* how links behave in mirror tab. */

.mirrortab_back a:link,
.mirrortab_back a:visited,
.mirrortab_active_back a:link,
.mirrortab_active_back a:visited {
    color: white;
    text-decoration: none;
}
.mirrortab_back a:hover,
.mirrortab_active_back a:hover {
    color: #e0e0ff;
    text-decoration: none;
}
/* The AJAX notifier */

#ajax_in_progress {
    background: #32CD32;
    color: white;
    text-align: center;
    font-weight: bold;
    font-size: 18pt;
    padding: 3px;
    width: 100%;
    position: fixed;
    top: 0;
    left: 0;
}
/* styles for category tabs */

ul#cats {
    margin: 0;
    padding: 0;
    list-style: none;
}
ul#cats li {
    float: left;
    margin: 0;
    padding: 0;
}
ul#cats li a {
    display: block;
    padding: 8px;
    border: solid 1px #888;
    background: #1D2E3E;
    color: white;
    margin: 0 2px 1px 0;
    text-decoration: none;
}
ul#cats li a.chosen {
    background: #000000;
}
ul#cats li a:hover,
ul#cats li a.chosen:hover {
    background: #000000;
}
.category {
    border: solid 1px #888;
    padding: 10px;
    clear: both;
    background: #252525;
}
/* end */

.karmabars img {
    padding-right: 1px;
    padding-left: 0px;
}
#karma_menu {
    position: absolute;
    border: 1px solid black;
    z-index: 100;
}

