css:
@-moz-document url-prefix("https://www.synchronkartei.de/") {
@media (min-width: 768px) {
	.breadcrumb {
	    width : 720px;
	}
}
@media (min-width: 992px) {
	.breadcrumb {
	    width : 970px;
	}
}
@media (min-width: 1200px) {
	.breadcrumb {
	    width : 1140px;
	}
}
h1 {
	margin-top : 0px;
}
.breadcrumb {
	position : fixed;
    top      : 100px;
    z-index  : 1029;
}
.page {
    padding-top : 54px;
}
@media (max-width: 992px) {
	.breadcrumb {
		top   : 68px;
	    
	}
    .page {
	    padding-top : 20px;
	}
}
@media (max-width: 768px) {
	.breadcrumb {
	    right     : 20px;
        left      : 20px;
		width     : auto;
	}
}
}
