css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arxivist.com") {
  
  /*** Palette ***/
  :root {
    --default-bkgd-color: #282828;
    --light-bkgd-color-1: #333333;
    --light-bkgd-color-2: #3f3f3f;
    --default-color: #aaaaaa;
    --blue-color: #129bfa;
    --blue-color-2: #0470bb;
    --select-bkgd: #3b3a36;
    --button-bkgd: #1b1a17;
  }
  
  
  
  /** Bkgd **/
  .ui-layout-pane {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /** Links **/
  a {
    color: var(--blue-color-2);
  }
  
  /** Logo **/
  div.content img {
    filter: invert(84%);
  }
  
  /** Search **/
  
  .background.search_button_div, .background.search_field_div {
    background-color: var(--button-bkgd);
  }
  
  /* Search select */
  .search_field {
    background-color: var(--select-bkgd);
    color: var(--default-color);
  }
  
  /* Search button */
  .search_button {
    background-color: var(--button-bkgd);
    color: var(--default-color);
  }
  
  /** Google Authentication **/
  
  .west-toggler, .logout.right_block {
    background-color: var(--button-bkgd);
    color: var(--default-color);
  }
  
  /** Calendar Days List **/
  .frame ul li {
    background-color: var(--light-bkgd-color-1);
  }
  
  .frame ul li.active {
    background-color: var(--light-bkgd-color-2);
    color: var(--default-color);
  }
  
  /** Loading symbol **/
  img[src="src/images/Loading_icon.gif"] {
    filter: invert(84%);
  }
  
  /** List of Papers **/
  tr {
    color: var(--default-color);
  }
  
  /* visited */
  tr[style="color: blue;"] {
    color: var(--blue-color-2) !important;
  }
  
  /* hover + visited */
  tr:hover {
    color: var(--blue-color) !important;
  }
  
  /* Alternating row colors */
  tr:nth-child(odd) {
    background-color: var(--light-bkgd-color-1);
  }
  
  tr:nth-child(even) {
    background-color: var(--default-bkgd-color);
  }
  
  th {
    background-color: var(--light-bkgd-color-2);
  }
  
  /** Buttons **/
  
  /* Link Pop-up */
  a[target="_blank"] {
    filter: invert(80%);
  }
  
  /* Unselected */
  tr:nth-child(odd) img.upvote, tr:nth-child(odd) img.downvote {
    filter: invert(80%);
  }
  
  tr:nth-child(even) img.upvote, tr:nth-child(even) img.downvote {
    filter: invert(84%);
  }
  
  /* Selected */
  tr:nth-child(odd) img.neutralvote {
    filter: invert(80%);
  }
  
  tr:nth-child(even) img.neutralvote {
    filter: invert(84%);
  }
  
  /*** Divider ***/
  .ui-layout-resizer {
    background-color: var(--light-bkgd-color-2);
  }
  
  /*** 'Starting Off' - Initial Pop-up Pane ***/
  
  .ui-widget-content {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  .ui-widget-header {
    color: var(--default-color);
  }
  
  /**** arxiv Preview ****/
  
  /* First-time users preview */
  body {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /* After configuration, according to arxiv.org theme */

}
