css:
@-moz-document url-prefix("https://www.reddit.com/r/valkyrie_en/") {
#sr-header-area {
    background: rgba(21, 27, 33, .4);
    line-height: 30px;
    text-transform: uppercase;
    font-size: 9px;
    color: #fff;
}
#sr-header-area a {
    font-size: 13.5px !important
}
#sr-header-area #srLeftContainer {
    padding-left: 15px;
}
#srLeftContainer,
#RESShortcutsViewport,
#RESShortcutsEditContainer {
    max-height: 30px;
}
#header {
    box-shadow: inset 0 -35px rgba(21, 27, 33, .4);
    background: url(http://i.imgur.com/WpRJ9S0.png) no-repeat center;
}
#header .redditname a {
    margin: inherit;
    display: block;
    position: relative;
    left: 112.2%;
    height: 261px;
    width: 449px;
    bottom: -24px;
    background: url(http://i.imgur.com/WKhRnlf.png) no-repeat center top;
}
#header .pagename a {
    font-size: 0px;
}
#header #header-img {
    opacity: 0
}
#header .tabmenu {
    position: absolute;
    margin: 0;
    bottom: 6.75px !important;
    left: 15px;
    text-transform: uppercase;
	font-size: 13.5px !important
}
#header #header-bottom-right {
    top: 233px;
}
#header .user {
    top: 0px;
    right: 223px;
    font-size: 13.5px;
    font-style: normal
}
.res div#userbarToggle.userbarShow {
    top: 0px;
}
#RESShortcutsEditContainer {
    right: 15px;
}
#header ul.tabmenu {
    bottom: 8.55px;
}
}
