css:
@-moz-document regexp("http://www.nicovideo.jp/watch/(?!.*nm).*$") {
/*Simple and Darker Nicovideo Player for HTML5*/

/*Disable comment*/
/*
#CommentRenderer,.MarqueeContainer,.CommentPostContainer,.CommentOnOffButton {
	display: none;
}
*/

/*If autoplay is installed*/
/*
.VideoStartButtonContainer{
    display:none;
}
*/

/*Remove ad & news*/
.NicoSpotAdContainer,
.BottomContainer-side,
.BottomContainer-adContainer,
.IchibaContainer,
.MarqueeContainer {
    display: none;
}

/*Remove "Switch to Flash banner"*/
.SwitchToFlashLead {
    display: none;
}

/*Remove premium plan ad*/
.PreVideoStartPremiumLinkContainer,
#siteHeader #siteHeaderInner #siteHeaderNotification:not(.siteHeaderPremium) a.siteHeaderUpgrade{
    display: none;
}

/*Theme*/
body {
    background: #444;
}

/*Font color*/
a,
.TagItem-name,
.VideoUploadDateMeta-title,
.VideoViewCountMeta-title,
.CommentCountMeta-title,
.MylistCountMeta-title,
.CategoryRankMeta-title,
.CategoryRankMeta-categoryName,
.PlaylistItemContent-title,
.Link,
.ContentTree-parentSummaryLabel,
.RouterLink,
.VideoDescriptionExpander .VideoDescriptionExpander-switch,
.BottomContainer-cardTitle{
    color: #DDD;
}

/*Vid info*/
.FollowButton{
    background-color:#444;
    border:2px solid #AAA;
}
.TagContainer-editButton {
    transition:0.3s;
    border-radius: 2px;
    background: #666;
}
.TagContainer-editButton:hover {
    transition:0.3s;
    border-radius: 2px;
    background: #AAA;
}
.TagItem {
    background: #666;
    border: none;
    border-radius: 2px;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.TagContainer.is-editing{
	border-radius:2px;
	background:#AAA;
}
.TagEditForm-submitButton{
background-color:#555;
}

.VideoOwnerUploadedLink{
	border:0;
}
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand:before {
    border-top: 8px solid #DDD;
}
.VideoDescriptionExpander .VideoDescriptionExpander-switchCollapse:before {
    border-bottom: 8px solid #DDD;
}
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand {
    background: linear-gradient(90deg, transparent 0, #333 16%);
}
.VideoUploadDateMeta-dateTimeLabel,
.FormattedNumber,
.CommentCountMeta-counter,
.MylistCountMeta-counter,
.CategoryRankMeta-yesterdayRank {
    color: #CCC;
}
.HeaderContainer-videoTitle>.VideoTitle,
.VideoDescription-html,
.VideoOwnerInfo-pageLink {
    color: white;
}
.VideoMetaOverflowMenuContainer-menu{
	background:#444;
}

/*Search box*/
.SearchBox {
    background: transparent;
}
.SearchBox-optionMenu {
    background: #444;
}
.SearchBox-optionMenuItem:hover {
    background: #666;
}
.SearchBox-input{
	box-shadow:none;
	background-color:#444;
    color:#FFF;
}

/*Player*/
.MainContainer-commentPanel {
    display: none;
}
.MainContainer-player {
    width: calc(100% - 25px);
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.VideoContainer {
    width: 1000px;
    height: 562px;
}
.VideoMetaContainer>*,
.VideoMetaOverflowMenuContainer {
    border-left: 1px solid #777;
    box-shadow: -1px 0 0 0 #555;
}
.VolumeBar-progress,
.SeekBar-played,
.SeekBarHandle-handle{
    background-color:#AAA;
}
.SeekBar-buffered{
    background-color:#666;
}
.ControllerButton:hover svg{
	fill:#666;
}

/*Comment*/
.CommentPostContainer-commandInput,
.CommentPostContainer-commentInput {
    background-color: #777;
}
.CommentPostContainer-commandInput {
    border-right: 1px solid #444;
}
.CommentPostButton.ActionButton {
    transition:0.3s;
    background-color: #555;
}
.CommentPostButton.ActionButton:focus,
.CommentPostButton.ActionButton:hover{
    transition:0.3s;
    background-color:#AAA;
}

/*Playlist*/
.BottomContainer-playlistShuffleButton,
.BottomContainer-cardButton-inactive{
    transition:0.3s;
	background: #666;
    border: none;
    border-radius: 2px;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.BottomContainer-playlistShuffleButton:hover,
.BottomContainer-cardButton-active{
	transition:0.3s;
    background-color: #AAA;
    border: none;
    border-radius: 2px;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.BottomContainer-card {
    background: #777;
    border-radius: 2px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.1);
}
.BottomContainer-card .PlaylistContainer {
    border-radius: 2px;
    background: #888;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.PlaylistItemContent {
    border-radius: 2px;
}
.PlaylistCardItem.is-playing .PlaylistItemContent {
    background: #444;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.PlaylistItemContent {
    background: #666;
    box-shadow: 2px 2px 2px rgba(0, 0, 0, 0.1);
}
.ContentTree-parentSummary .Link {
    background-color: #666;
}
.PlaylistPager-arrow {
    transition: 0.3s;
    background-color: #888;
    fill: #666;
}
.PlaylistPager-arrow:hover {
    transition: 0.3s;
    background-color: #888;
    fill: #555;
}
.PlaylistPager-arrow.is-disabled {
    fill: #AAA;
}
.PlaylistPagerArrowRightButton {
    border-top-left-radius: 2px;
    border-bottom-left-radius: 2px;
}

.ContentTree-parentSummary .Link:hover{
    background-color: #888;
}

/*Footer*/
.FooterContainer-group {
    display: none;
}
.FooterContainer-logo {
    margin-bottom: 6px;
}
}
