css:
@-moz-document url-prefix("https://www.zhihu.com/question/") {
@media print {
    body {
        font-size: 15px;
        word-wrap: break-word;
        color: #000000;
    }
    .RichText blockquote {
        padding-left: 1em;
        margin: 1em 0;
        color: #222222;
        border-left: 4px solid #cfd8e6;
    }
    .Card {
        margin-bottom: 14px;
        background: #fff;
        border: none;
        box-shadow: none;
        box-sizing: border-box;
    }
    .Sticky.is-fixed,
    .QuestionHeader-side,
    .Question-sideColumn,
    .QuestionButtonGroup,
    .CornerButtons--showBackToTopButton,
    .ShareMenu,
    .AnswerItem-extraInfo,
    .QuestionAnswers-answerAdd,
    .CornerButtons,
    .PageHeader,
    .AppHeader-inner {
        display: none;
    }
    .QuestionHeader-main {
        width: 986px;
        padding: 0 24px;
        box-sizing: border-box;
    }
    .Question-mainColumn {
        width: 986px;
        padding-bottom: 20px;
    }
    .List-item:not(:last-child):after {
        position: absolute;
        bottom: 0;
        display: block;
        width: calc(100% - 48px);
        border-bottom: 1px solid #cccccc;
        content: "";
    }
    .VoteButton.is-active {
        color: #2d84cc;
        background: #ebf3fb;
        border-color: #ebf3fb;
    }
}
}

@-moz-document url-prefix("https://zhuanlan.zhihu.com/p/") {
@media print {
    .Post-SideActions button.like {
        display: none;
    }
    .ColumnPageHeader-Wrapper {
        display: none;
    }
    .Post-SideActions button {
        display: none;
    }
    .FollowButton {
        display: none;
    }
    .Post-Main .Voters {
        display: none;
    }
    .Comments-container,
    .Recommendations-Main {
        display: none;
    }
    .CornerButtons {
        display: none;
    }
    .LikeButton.is-active:hover {
        background-color: rgba(0, 132, 255, .1);
    }
    .LikeButton.is-active {
        background-color: rgba(0, 132, 255, .1);
        color: #0084ff;
    }
    .Sticky RichContent-actions is-fixed is-bottom {
        display: none;
    }
    .Button ContentItem-action Button--plain Button--withIcon Button--withLabel {
        display: none;
    }
    .Button Button--plain Button--withIcon Button--withLabel {
        display: none;
    }
    .Button--withIcon.Button--plain {
        display: none;
    }
}
}
