css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://diesel.elcat.kg"), url-prefix("https://diesel.elcat.kg") {
  /*Пост*/
  .post_block { background: #272C2D; }
  .post_block h3 { background: #0F3854; }
  /*Цитата*/
  blockquote.ipsBlockquote { background: #202424; font-size: 14px; padding: 4px 10px; border: 2px solid #1c580c; border-top: 0px;}
  p.citation { background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#333333), color-stop(100%,#111111)); padding: 4px 10px; border-right: 2px solid #1c500c; border-left: 2px solid #1c500c; border-top: 0px; }
  cite.ipb {background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#333333), color-stop(100%,#111111)); border-right: 2px solid #1c500c; border-left: 2px solid #1c500c; border-bottom: 0px; }
  /*Код*/
  pre._prettyXprint, pre.prettyprint, code.prettyprint {background-color: #293134 !important; padding: 5px; border: 1px solid #1c580c; color: #E0E2E4;}
  .pln { color: #E0E2E4; } /*text*/
  .com { color: #A082BD;} /*preproc*/
  .kwd { color: #93C763; } /*keyword*/
  .str { color: #EC7600; } /*string*/
  .lit { color: #FFCD22; } /*number*/
  .typ { color: #678CB1; } /*type*/
  .atn {color: #B3B689;}
  .atv {color: #E1E2CF;}
  .tag {color: #678CB1;}
  .dec, .var {color: #66747B;}
  .pun, .opn, .clo { color: #E0E2E4; }
  /*Ответ*/
  .ipsBox_container {background: #424242; }
  html, body { background-color: #272C2D; color: #BABDB6;}
  .cke_top {background: #AAAAAA !important;}
  .cke_skin_ips textarea.cke_source { background-color: #272C2D; color: #BABDB6;}
  .cke_skin_ips .cke_wrapper { border: 2px solid #333333 !important; background-color: #333333 !important;}
  /*Фон по бокам*/
  .ipsBox {background: #333333; }
  #content { background: #000000; }
  #submenu_my { background: #CCCCCCCC;}
  #content, .main_width { width: 98% !important;}
  .ipsSideBlock { background: #0E1214; }
  .ipsHeaderMenu { background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#111111), color-stop(100%,#333333)); }
   /*Текст*/ 
  .post_body .post { color: #BABDB6;}  
   a { color: #729FCF; }
  .ipsType_textblock { color: #BABDB6; }
  /*Страница тем*/
  tr.__topic[style] { background: #484848 !important; }
  tr.unread[style] { background: #272C2D !important; }
  .post_ignore { background: #222224; }
  .unread { background-color: #272C2D; }
  .row2, .post_block.row2 {background-color: #222222;}
   tr.row1 { background-color: #333333; }
  .row1, .post_block.row1 { background-color: #333333; }
  .altrow { background-color: #484848;}
  .unread .altrow { background-color: #222222;}
}
