css:
@-moz-document domain("coldfilm.tv"), domain("coldfilm.ru"),domain("coldfilm.info"),domain("coldfilm.cc") {
/*--------------------Реклама----------------*/ 

#adv-block,noindex{
    display: none !important;
}


/*-------------- Центральный блок ----------*/ 

#container_wrap{
	width:100% !important;
    margin:0 !important;
}

.cols {
padding:0 !important;
}

#allEntries>div>.kino-item{
width:30% !important;
}

.catPages1{
font-size: 30px  !important;
}

.navigation a, .navigation b, .swchItemA, .swchItem{
padding: 10px 17px   !important;
}
.col-side,.first-menu,.carousel-wr,.second-menu,.berrors,.bottom,
.main-items.clearfix.grid.grid-thumb>div[align="center"]
{
    display: none !important;
}

.cols:before{
content:none !important;
}

/*-------------- Заголовок -----------------*/ 

#header #hbanka{
    display: none !important;
}


#header a img{
    margin:0 auto !important;
}


/*-------------- Навигация -----------------*/ 

.hnav li a {
    font-size: medium !important;
    padding:10px 5;
}
#hnav_c_bg,#hnav_l_bg,#hnav_r_bg,.hnav li{
    background: 0 !important;    
}
#hnav_c_bg{
    padding: 5px;
}

/*-------------- Сайдбары ------------------*/ 


#side_left,#side_right {
    display: none !important;
}


/*-------------- Центральный блок ----------*/ 

/*-------------- Список сериалов -----------*/ 


#side_center{
    width:80% !important;
    margin:0 auto !important;
}

#side_center>div:not(#allEntries) {
    display: none !important;
}

/*-------------- Карточка сериала ----------*/ 

#side_center>#allEntries>div>div:nth-child(2n){
    display: none !important;
}

#side_center>#allEntries>div>div:nth-child(2n-1):not(.catPages1){
    width: 350px!important;
    height:560!important;
    
    display: block!important;
    float: left!important;
    margin: 10px 20px!important;
}
#side_center .catPages1{
    clear:both !important;
    font-size: large !important;
    background: 0 !important;
}
#side_center span{
    margin: 0 2px;
    padding: 0 10px;
}

/*-------------- Футер --------------------*/
}
