css:
@-moz-document url("https://app.simplenote.com/") {
.note #txtarea {
    font-size: 1em !important;
    color: #ddd;
    font-family: monospace !important;
}
.app {
    background: #293134;
}
.searchfield {
    border: 1px solid #000000;
    box-shadow: none !important;
}
.toolbar {
    border-bottom: 1px solid #000000;
}
.sideview {
    border-bottom: 1px solid #000000;
}
.tagbar {
    border-bottom: 1px solid #000000;
    left: 400px !important;
}
.sidebar {
    border-right: 1px solid #000000;
    width: 465px;
}
.wrapper {
    bottom: 0px;
}
.footer {
    display: none;
}
.searchfield {
    color: #ddd;
}
.notes li {
    padding: 5px;
    text-shadow: none;
    border-bottom: 1px solid #000000;
}
.notes li .note-preview-title {
    color: #ddd;
    font-size: 15px;
}
.note #txtarea {
    padding: 20px;
}
.note #static_content {
    color: #ddd;
}
.notes li:hover {
    background-color: #555;
}
.notes li.selected {
    background: #555;
}
.popover .menu.tags {
    z-index: 0 !important;
    position: static !important;
    visibility: visible !important;
    padding-left: 5px;
    margin-top: 5px;
}
.popover .menu li a, .popover .menu h4 {
    color: #ddd;
    background: #293134;
    text-shadow: none;
    border-bottom: 1px solid #000000;
}
.popover .menu .taglist {
    overflow: visible;
}
.notes {
    left: 132px;
    border-left: 1px solid #000000;
}
.note {
    left: 461px;
}
.sideview span.tail {
    visibility: hidden;
}
.tagmenu.button .menu li.trash a.empty {
    background: #555;
    text-shadow: none;
}
.toolbar .tools {
    left: 480px;
}
}
