@-moz-document domain("forum.babyklar.dk") {
  /* get rid of tickers */
  img[src*="daisypath.com"],
  img[src*="lilypie.com"],
  img[src*="thebump.com"]
    {display:none!important}

  /* all signatures */
  div.signature {display:none!important}

  /* ALL smilies */
  img[src^="style_emoticons"] {display:none!important}

  /* make the top part drop down */

  #submenu , #logostrip
  {height:10px!important;
   overflow:hidden!important;
   width:auto!important;
   opacity:0!important}

   #submenu:hover
  {height:auto!important;
   width:auto!important;
   opacity:1!important;
   border-bottom:1px solid #aaa
  }


  /* 123hjemmeside vÃ¦k ... hvis det er reklame */
