css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://chat.napalmriot.com/") {

#window-napalm-content {
   width: 79% !important;
}

.window-users-box {
   width: 20% !important;
}

#window-napalm-input {
   width: 99.5% !important;
}

#chat {
   padding: 0 0px 0px !important;
}

#tfr-a {
   display:none !important;
}

.window-topic {
   font-size:12px;
}

.ts {
   font-size: 8px;
}

.item .avatar{
display:none !important;
}

.item-user .avatar{
display:none !important;
}

.item-user:hover .avatar{
display:inline !important;
}

.username {
   font-weight: 100 !important;
}

.window-box {
   font-size:10px !important;
}

#header{
   display:none;
}

#toolbar{
      display:none;
}

#footer{
      display:none;
}


#content{
       margin-top:0px !important;
       margin-left:0px;
       background:transparent!important;
}

#chat {
background:transparent;
}

#side-left{
display:none;
}

.side-left .side-right .margins {
background:#000000;
}

#content{
background:#000000;
}


.bottom, .chunk, .top{
display:none;
}

.side-right {
display:none;
}

.side-left {
display:none;
}



#chat{
display:block;
background:transparent;
}

#content{
background:transparent;
}

.ownmessage{
color:#DDDDDD!important;
background:transparent!important;
}

#window-napalm-input-button{
display:none;
}


}
