css:
@-moz-document url-prefix("https://gaming.youtube.com/") {
#author-photo { display: none !important; }
.yt-live-chat-text-message-renderer-0 {
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    background: #222222;
    font-size: 13px;
    line-height: 20px;
    color: #898395 !important;
    padding: 4.5px 10px;
}

#author-name.yt-live-chat-text-message-renderer {
    font-weight: bold !important;
}
#message.yt-live-chat-text-message-renderer {
    color: #ffffff !important;
}

.yt-live-chat-header-renderer-0, .yt-live-chat-message-input-renderer-0 {
    background: #222222;
    color: #706a7c;
}
#author-name, .yt-live-chat-text-input-field-renderer-0 #label.yt-live-chat-text-input-field-renderer {
    color: #706a7c;
    font-size: 12px;
    font-weight: normal !important;
    text-shadow: none;
}
.yt-live-chat-message-input-renderer-0 #avatar.yt-live-chat-message-input-renderer {
    display: none;
}
.yt-live-chat-message-input-renderer-0 {
	border-top: 1px solid rgba(255,255,255,0.1);
}
.ytg-watch-page-1 yt-live-chat-renderer.ytg-watch-page {
    width: 200px;
}
.yt-live-chat-renderer {
    width: 200px;
}
.yt-emoji-picker-renderer-0 {
    max-width: 200px;
}
.yt-live-chat-message-input-renderer-0 {
    padding-left: 0px;
    padding-right: 0px;
}
/* Chat Header */

.yt-live-chat-header-renderer-0 {
    box-shadow: none;
    border-bottom: 1px solid rgba(255,255,255,0.1);
    text-shadow: none;
    font-size: 12px;
    padding: 0;
    height: 40px;
}
.yt-live-chat-header-renderer-0 #title.yt-live-chat-header-renderer {
    font-weight: normal !important;
    color: #706a7c !important;
    text-align: center;
}

.yt-live-chat-item-list-renderer-0 #items.yt-live-chat-item-list-renderer { padding: 0; }
}
