css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("experts-exchange.com") {
.relatedSolutions,
.hasMouseOver,
.allZonesMain,
#zoneAdvertisment,
.freePAQ,
.ontopBanner,
.startFreeTrial,
#pageRight,
[id^="relatedSolutions"]
{display:none !important}


/* simplify the question */
/* full width */
div[class="bc bcQuestion"] {
width:904px; padding:15px!important;margin:5px 0 0 0!important;border:1px dashed #999!important;
font-family:Verdana!important;
}
/* other font */
div[class="bc bcQuestion"] > h3+h3 {display:none!important}
h1.titleHeader {color:#7D868F!important;font-weight:normal!important;font-size:22px!important}


/* answers full width  and nicer */
div.bbg div.bbg {
border:1px dashed #999!important;width:890px!important;padding:10px!important;margin:0!important;
font-family:Courier New!important;font-size:13px!important
}

table.h {
width:914px!important;
}

/* let's enhance expert comments some more */
div.expExpBlue {
  border: 3px solid red!important;
}

div.acceptedHeader {
  border: 3px solid green!important;
}
div.answer, div.bbg { width: 914px!important }


/* HEADER */
#pageHeader {height:27px!important;overflow:hidden!important;width:auto!important;opacity:0!important}
#pageHeader:hover {height:auto!important;width:auto!important;opacity:1!important;border-bottom:1px solid #aaa}
#breadCrumb {width:100%!important;float:right!important;margin:-16px 0 3px 0!important}
#breadCrumb::before {content: "\25BC ";font-size:11px;font-weight:bold;
padding:2px;color:#666;-moz-border-radius:2px;}


}
