css:
@-moz-document domain("crucible") {
html,
body,
div,
applet,
object,
iframe,
h1,
h5,
h6,
a,
abbr,
acronym,
address,
big,
cite,
code,
del,
dfn,
em,
font,
img,
ins,
kbd,
q,
s,
samp,
small,
strike,
strong,
sub,
sup,
tt,
var,
b,
u,
i,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td,
.review-header,
.review-header {
    background: rgb(175, 169, 140);
}
div:hover,
comment:hover {
    background: rgb(175, 169, 140);
}
.content-fixed,
.dashboard-v2 #content-column .toolbar,
#stream .article-date-separator,
.aui-button,
a.aui-button,
.aui-button:visited {
    background-color: #6c6f61;
}
.aui-header {
    background: #6e7957;
}
.article-summary .article-verb,
.article-summary .article-metadata {
    color: black;
}
.aui-button:active,
.aui-button.active,
.aui-header .aui-button-split-more.active,
.aui-button-subtle.aui-button:active,
.aui-button-subtle.aui-button.active {
    background: #cac6b6;
}
.aui-button.aui-button-primary,
.aui-button.aui-button-primary:visited,
.aui-header .aui-button.aui-button-primary {
    background: #cac6b6;
}
#quick-access .tabbed-filter {
    background: rgba(108, 111, 97, 0.46);
}
.welcome-mat-small {
    background-color: rgba(108, 111, 97, 0.46)
}
a {
    color: #ffffff !important;
    text-shadow: 3px 0px 3px rgba(0, 0, 0, 0.4);
}
.cells-empty {
    background-color: rgb(175, 169, 140);
}
.diffNav {
    background-color: rgba(108, 111, 97, 0.61);
}
.inlineSource td.author.cell-empty,
.inlineSource td.revision.cell-empty {
    border-color: #FFFFFF;
    background-color: rgba(108, 111, 97, 0.71);
}
.activeFrx .diffSkipped td {
    background-color: #8c8770;
    border: 3px dashed #ccc;
}
.diffSkipped td {
    height: 21px;
    background: none;
}
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox:not(.aui-dropdown2-disabled):hover,
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio:not(.aui-dropdown2-disabled):hover,
.aui-dropdown2.aui-style-default:hover a:not(.aui-dropdown2-disabled):hover {
    background-color: #6e7957;
}
.aui-dropdown2.aui-style-default {
    background-color: #6c6f61;
}
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-checkbox,
.aui-dropdown2.aui-style-default:hover .aui-dropdown2-radio,
.aui-dropdown2.aui-style-default:hover a {
    background-color: rgb(175, 169, 140);
}
#column-content {
    background-color: #afa98c;
}
#content-column .toolbar {
    background-color: rgba(108, 111, 97, 0.34);
}
.review-table.sortable thead th {
    background-color: rgba(240, 240, 240, 0.08);
}
.aui-header .aui-dropdown2-trigger.active,
.aui-header a:focus,
.aui-header a:hover,
.aui-header a:active {
    background-color: #6c6f61;
}
}
