css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
    * {
        font-family: "Linux Biolinum O", Georgia !important;
    }
    html {
        color: #222 !important;
        background: none repeat scroll 0 0 #222222 !important;
    }   
    body, .zci, .result__snippet {
        color: #aaa;
        font-size: 14px;
        background: none repeat scroll 0 0 #222222 !important;
    }
    .cw--c {
       margin-left: auto;
    }
    .cw {
        position: relative;
        padding: 0 7px;
        max-width: 1008px;
        margin-left: 0;
        margin-right: auto;
    }
    .tile__title {
         color:initial;
    }    
    .tile__expand {
        background-color: #383838;
        border-top: 1px solid #444;
    }
    .tile--info .info {
        border-top: 1px solid #404040;
    }
    .result, .results, .results_links, .highlight_v, .result__snippet {
        background-color: #222222 !important;
        max-width: 65em;
    }
    .result__pagenum--side {
       padding: 0;
       background-color: #282828;
        color: #666;
    }
    .results highlight {
        background-color: #333 !important;  
    }
    .header, header cw {
    }
    .header__search {
        margin-right: 19px;
    }
    .results--sidebar--mid .btn--top {
        position: absolute;
        left: 16px;
        top: 24px;
    }
    .result__badge {
       top: 2px;
    }
    .result__menu:hover {
        color: #e0972b !important;
        text-decoration: underline;
    }
    #links .results_links_deep {
        border: 0px none !important;
    }
    .results_links, .results_zero_click, .results_disambig, .results_links_more, .results_category_more, .results_disambig_more {
        border: 0px none !important;
    }
    .result, .cw .results--didya, .cw--c .results--didya {
        background-color: #222222;
    }
    .btn--top {
        background-color: rgb(222, 88, 51);
        color: #222;
    }
    .btn--top:hover {
        background-color: rgb(242, 108, 71);
        color: #222;
    }
    .btn--primary, .btn {
        background: #333;
        border: 1px solid #333;
        color: #888;
    }
    .cloudsave {
        border-radius: 2px;
        padding: 1em;
        background: none repeat scroll 0% 0% #282828;
        text-align: center;
        margin: 0px 0px 1em;
    }
    .set-reset, .set-bookmarklet {
        border-radius: 3px;
        margin: 1em 0px;
        background: #282828;
        border: 1px solid #333;
        text-align: center;
    }
    .set-main .frm__label {
        font-size: 1.3em;
        padding: 0px;
        font-weight: 400;
        color: #ccc;
    }
    .frm__switch__label {
        background: #333;
        border: 1px solid #333;
        color: #888;
    }
    .frm__select select {
        background: #333;
        border: 1px solid #333;
        color: #888;
    }
    .frm__select {
        border-radius: 3px;
        border: 1px solid #383838;
        background-color: #222;
    }
    .frm__select:after {
        background-color: #333;
        border-color: #888 transparent transparent;
    }
    .msg--box {
        background: rgba(231, 255, 0, 0.20) none repeat scroll 0% 0%;
        color: rgba(231, 255, 0, 1);
        border: 1px solid rgba(231, 255, 0, 0.3);
    }
    .badge--ad {
        background-color: #274771;
    }
    #search_button {
        padding-top: 2px;
        padding-bottom: 1px;
        padding-top: 3px;
        padding-top: 3px;
        background: #333;
    }
    #search_form_input_clear {
        padding-top: 2px;
        padding-bottom: 1px;
        padding-top: 3px;
        padding-top: 3px;
        margin-right: 40px;
        margin-bottom: 3px;
    }
    .search__button, .search__button--hero {
        margin-top: 2px;
    }
    .search--hover #search_button {

        background: #383838;
    }
    .search--hover {
        background: #333;
    }
    .header__button--menu, .header__button--menu highlight{
        background-color: #333 !important;
        border: 1px solid #222;
        /*margin: 15px;*/
    }
    .has-active-zci .header-wrap, .has-active-zci {
        background-color: #333333;
        border: none;
    }
    .region-indicator {
        background-color: #333333;
        margin: 15px;
        padding: 5px;
    }
    .popover__box {
        background-color: #333333;
    }
    .region__body {
        background-color: #333333;
    }
    .popover--dk:target, .popover--dk.is-showing {
        background: none repeat scroll 0% 0% rgba(21, 21, 21, 0.7);
        z-index: 100;
    }
    .nav-menu--slideout {
        background: none repeat scroll 0% 0% #181818;
        border-left: 0px solid #222;
    }
    .detail {
        -moz-box-shadow: inset 0 1px 0 rgba(0,0,0,0.01),inset 0 -1px 0 rgba(0,0,0,0.01);
        -webkit-box-shadow: inset 0 1px 0 rgba(0,0,0,0.01),inset 0 -1px 0 rgba(0,0,0,0.01);
        box-shadow: inset 0 1px 0 rgba(0,0,0,0.01),inset 0 -1px 0 rgba(0,0,0,0.01);
        -moz-box-sizing: border-box;
        -webkit-box-sizing: border-box;
        box-sizing: border-box;
        background-color: #181818;
        overflow: hidden;
        clear: both;
        border-top: 1px solid #050505;
        border-bottom: 1px solid #de5833;
        position: relative;
        top: 0;
        left: 0;
        width: 100%;
        z-index: 1;
    }
    .has-zcm .header-wrap:before, .has-zcm .header-wrap:after, .has-zcm .header:after {
        box-shadow: none;
        background: none;
    }
    .badge--official {
        background-color: #447799;
    }
    .header-wrap, header_wrapper {
        background-color: #333;
        height: 8em;
    }
    .header__button--menu {
        border: 1px solid #222;
        /*margin-top: 17px;*/
    }
    /*
    .header--aside__msg {
        right: 16px;
        top: -15px;
        font-size: 12px;
    }
    */
    b {
        color: #5588aa !important;
    }
    .search--header {
        background-color: #222222;
        height: 40px;
        border: 0px solid #444444;
    }
    .search__input, .search__input--adv {
        color: #CCC;
        padding: 3px;
    }
    search, .search--adv {
        background-color: #181818;
        border: 1px solid #333;
        color: #ccc;
    }
    .badge-link {
        -moz-border-radius: 4px;
        -webkit-border-radius: 4px;
        border-radius: 4px;
        color: #ccc;
        background-color: #1a1a1a;
        border: 1px solid #3f3f3f;
    }
    .site-wrapper:before {
        background-color: #4477aa;
    }
    a {
        color: #76C7FF !important;
    }
    .mapview__close, .metabar__mode {
        background-color: #304974;
    }
    .tile {
        background-color: #333;
        border: 1px solid #383838; 
    }
    .tile--m {
        text-align: center;
        color: #888;
        background-color: #363636;
    }
    .tile--m:hover {
        text-align: center;
        background-color: #444;
    }
    .tile-nav.can-scroll, .tile-nav.can-scroll::after {
        color: #fff;
        background-color: #206083;
        border: 3px solid #222;
    }
    .tileview--grid .metabar--fixed.is-stuck {
        box-shadow: 0 1px 0 rgb(51, 51, 51);
    }
    .tileview--grid .metabar--fixed.is-stuck {
        background-color: rgb(27, 27, 27);
    }
    .tile:hover {
        border: 1px solid #393939;
        border-radius: 2px
    }
    .tile__foot--news {
        color: #777;
        background: #282828;
    }
    .tileview .metabar--fixed {
        position: relative;
        top: 0 !important;
        background-color: #181818;
    }
    
    .tile-wrap {
        position: relative;
        top: 0;
        margin-top: 0;
        margin-bottom: 0;
    }
    .modal--popover--dk.is-showing {
        background: rgba(0,0,0,0.75);
    }
    #header_wrapper #header {
        background-color: #333333;
    }
    #search_form {
        border-bottom: 1px solid #202020 !important;
    }
    #search_form_input {
        border-color: #888888 !important;
    }
    #header_button {
        border: 1px solid #444444 !important;
    }
    #logo {
        display: none !important;
    }
    #logo_homepage {
        height: 161px !important;
        margin-bottom: 40px !important;
        padding-bottom: 1px !important;
        width: 300px !important;
    }
    .region-m .modal__box--region {
        width: 95%;
        max-height: 709px;
        background-color: #222;
        border: 1px solid #333;
    }
    .region__body {
        background-color: #222;
    }
    #side {
        background-color: #222222 !important;
    }
    #side_suggestions, #side_suggestions a {
        color: #A1A1A1 !important;
    }
    #side_wrapper2 {
        color: #DDDDDD !important;
    }
    #zero_click {
        background: none repeat scroll 0 0 #000000 !important;
    }
    #zero_click a {
        color: #76C7FF !important;
    }
    #zero_click a:visited {
        color: #76C7FF !important;
    }
    #zero_click .highlight {
        background: none repeat scroll 0 0 #F3F3F3 !important;
        border-color: #444444 !important;
    }
    #zero_click_header a {
        color: #444444 !important;
    }
    #zero_click_header a:visited {
        color: #444444 !important;
    }
    #zero_click_abstract .highlight {
        background: none repeat scroll 0 0 #222222 !important;
        border-color: #444444 !important;
    }
    #zero_click_topics .highlight {
        background: none repeat scroll 0 0 #222222 !important;
        border-color: #444444 !important;
    }
    .zci.is-active {
        border-bottom: 1px solid #DE5833 !important;;
    }
    .web-result:hover, .zero-click-result:hover {
        background: none repeat scroll 0 0 #000000 !important;
        border: 1px solid #BFBFBF !important;
        border-radius: 0px !important;
        cursor: default !important;
    }
    #links .icon_fav img, .icon_fav2 img {
        height: 24px !important;
        width: 24px !important;
    }
    .navbutton {
        color: #76C7FF !important;
    }
    input.navbutton:hover {
        border-bottom: 0px none !important;
    }
    a.large {
        color: #76C7FF !important;
    }
    a.large:visited {
        color: #D7B7FF !important;
    }
    .url, .url a {
        color: #63D684 !important;
    }
    .url a:visited {
        color: #D7B7FF !important;
    }
    .links_menu, .links_menu a {
        color: #FFC334 !important;
    }
    .links_menu a:visited {
        color: #D7B7FF !important;
    }
    .snippet, .snippet a {
        color: #E5E5E5 !important;
    }
    #tagline_homepage {
        font-size: 15px !important;
        color: #E5E5E5 !important;
    }
    #tagline_homepage a {
        color: #76C7FF !important;
    }
    #tagline_homepage a:visited {
        color: #76C7FF !important;
    }
    #dd-titleblock .dd-title {
        color: #E5E5E5 !important;
    }
    #introblock h3 {
        color: #E5E5E5 !important;
    }
    #introblock p {
        color: #E5E5E5 !important;
    }
    .header.cw, .header.cw--c {
        padding-left: 0;
        padding-right: 0;
        max-height: 78px;   
    }
    #footer_homepage_left {
        color: #B0B0B0 !important;
    }
    #footer_homepage_left a {
        border-right: 1px dotted #E5E5E5 !important;
        color: #B0B0B0 !important;
    }
    #footer_homepage_right a {
        border-left: 1px dotted #B0B0B0 !important;
        color: #B0B0B0 !important;
    }
    .modal .icon, .add_to_browser .icon, #footer_homepage .add_to_browser .icon, #footer_homepage .modal .icon {
        color: #666666 !important;
    }
    #more_menu li a {
        color: #1168CC !important;
    }
    .content-wrap--home {
        position: absolute;
        margin: auto;
        width: 100%;
        top: 24%;
        left: 0;
        right: 0;
        z-index: 2;
    }
    
    
    
    
    .results__sidebar {
        top: 33px;
        left: 1035px;
    }
    .module {
        background-color: #222;
    }
    .results--sidebar, .results--sidebar--alt {
        right: 20px;
    }
    
    
}
