css:
@-moz-document url("https://itch.io/dashboard") {
.dashboard_page .dashboard_columns {
    flex-direction: column;
}
.dashboard_page .dashboard_columns .left_col {
    order: 2;
}
.dashboard_page .dashboard_columns .right_col {
    width: 100%;
}
.dashboard_page .right_col .graph_container {
    width: 450px;
    float: left;
}
.dashboard_page .left_col .buttons {
    clear: both;
}
.dashboard_page .left_col .game_list {
    margin-left: -5px;
}
.dashboard_page  .left_col .game_list .game_row {
    margin-left: 5px;
    margin-bottom: 5px;
    box-sizing: border-box;
    width: 215px;
    height: 215px;
    float: left;
}
.dashboard_page .left_col .game_row .game_title {
    margin-top: 40px;
}
.dashboard_page .game_list .cover_image {
    width: 100%;
    height: 127px;
}
.dashboard_page .game_list .game_title,
.dashboard_page .game_list .game_links {
    margin-left: 0;
}
.dashboard_page .game_list .game_links {
    padding: 0;
}
.dashboard_page .game_list .publish_status {
    float: left;
    margin-right: 4px;
}
.dashboard_page .game_list .publish_status span {
    height: 36px;
    border-radius: 0;
    padding: 0;
    width: 4px;
    vertical-align: top;
}
.dashboard_page .game_list .publish_status a {
    display: none;
}
}
