    color: #201c2b!important;
    border-color: #B19DD8!important;
}

/**Media Player**/

.player-livestatus__online:before {
    background: #FF0000!important;
}

.player {
    color: #6441A4!important;
}

.player-play-overlay .player-button-play {
    fill: #6441A4!important;
}


/**Dashboard**/

.vod-vertical-nav__items {
    background: #222222!important;
}

.vod-vertical-nav {
    border-right: none!important;
    background: #222222!important;
}

.dash-widget__header {
    background: #222222!important;
    border-bottom: none!important;
}

.dash-widget__body {
    background: #171717!important;
    border: 1px solid #222222!important;
}

.dash-widget {
    box-shadow: none!important;
    background: none!important;
    border: none!important;
}

.dash-column {
    border-top: none!important;
}

.empty-grid {
    color: #B19DD8!important;
}

.dash-widget .dash-tabs__label {
    background: #6441A4!important;
    border: 1px solid #222222!important;
}

.dash-tabs__item--active .dash-tabs__label:after {
    border-bottom: none!important;
}

.dash-tabs__item--active {
    background: none!important;
}

.dash-tabs__item {
    border: none!important;
}
}

