css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://diesel.elcat.kg") {
  /*Пост*/
  .post_block { background: #F5F5F5; }
  .post_block h3 { background: #F5DEB3; }
  /*Цитата*/
  blockquote.ipsBlockquote { background: #FFF8DC; }
  p.citation { background: #DEB887; }
  /*Ответ*/
  .ipsBox_container {background: #FFFAFA; }
  body { background-color: #B0C4DE; color: #000000;}
  /*Фон по бокам*/
  .ipsBox {background: #333333; }
  #content { background: #FFFFF0; }
 /*Текст*/ 
  .post_body .post { color: #000000;}  
   a { color: #729FCF; }
  /*Страница тем*/
  .unread { background-color: #FFFAFA; }
  .row2, .post_block.row2 {background-color: #B0C4DE;}
  
  .breadcrumb a {
    color: #000000;
  font-size: 15px;}
  
  .breadcrumb {color: #000000}
  
  .maintitle {
    background: #696969;
    color: #fff;}
  
}
