css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  domain("mikrocontroller.net"),
  domain("www.mikrocontroller.net"),
  domain("embdev.net"),
  domain("www.embdev.net")
{
  body, #main {
    background-color: #000;
    color: #AAA;
    border: 0 none !important;
  }
  #attention, div.box, .homepagebox, .toc, #toc, #content > #homefloatright .sidebar-right-posts {
    background-color: rgba(255,255,255,0.125);
    color: #EEE;
    border: 0 none !important;
    background-image: none;
  }
  .homepagebox, .toc {
    display: inline-block !important;
  }
  .sidebar div.box {
    background-color: unset;
    border-bottom: 2px solid #000 !important;
  }
  .quoting-level-1 {
     color: #0d2 !important;
  }
  .quoting-level-2 {
     color: #3af !important;
  }
  .quoting-level-3 {
     color: #f30 !important;
  }
  .quoting-level-4 {
     color: #fa3 !important;
  }
  .quoting-level-5 {
     color: #ff0 !important;
  }
  .quoting-level-6 {
     color: #0ff !important;
  }
  .quoting-level-7 {
     color: #f0f !important;
  }
  .code table {
    background-color: #000 !important;
    border: 0 none !important;
  }
  .code table * {
    background-color: unset !important;
  }
  .forum .post .subject, .headrow {
    background-color: rgba(255,255,255,0.4) !important;
    color: #EEE !important;
  }
  .forum .post .subject a {
    color: inherit !important;
  }
  table.list {
    border: 0 none !important;
    background-color: rgba(255,255,255,0.1);
  }
  table.list tr.odd td {
    background-color: rgba(255,255,255,0.1) !important;
  }
  ul.attachments-images li, ul.attachments-files li {
    border-color: #000;
    background-color: transparent;
  }
  .sidebar {
    background-image: none;
    border: 0 none !important;
    background-color: rgba(255,255,255,0.07);
  }
  textarea, input {
    background-color: #000 !important;
    color: #fff !important;
    border: 0 none !important;
  }
  input:focus {
    box-shadow: initial !important;
  }
  hr {
    background-color: #000;
    height: 0 !important;
    border: #000 solid 1px;
  }
  header {
    overflow: visible;
    border-bottom: 1px solid #d4931c;
  }
  #top, header .navbar, header .dropdown-menu {
    background: unset !important;
    background-color: #202020 !important;
    border: 0 none;
  }
  header .dropdown-menu {
    border-top: 1px solid #d4931c !important;
    border-bottom: 1px solid #d4931c !important;
  }
  #top h1 {
    background-color: unset !important;
  }
  #top h1 object, header .navbar object, img.logo {
    filter: invert(1);
  }
  #top a {
    color: #fff !important;
  }
  a, h1#pageName small a {
    color: #ff8000 !important;
  }
  a:visited {
    color: #CC6000 !important;
  }
  a:active {
    color: #FFFF00 !important;
  }
  a:hover {
    color: #ffAA22 !important;
  }

  .navbar-default .navbar-nav li a:hover,
  .navbar-default .navbar-nav li.active a:hover,
  .sidebar-post-list li:hover
  {
    background-color: rgba(255,255,255,0.1) !important;
  }

  .navbar-default .navbar-nav li a:focus,
  .navbar-default .navbar-nav li.active a,
  .navbar-default .navbar-nav li.open a:focus,
  .navbar-default .navbar-nav li.open a.dropdown-toggle
  {
    background-color: rgba(255,255,255,0.2) !important;
    color: #ffAA22 !important;
  }

  .mw-highlight {
    background-color: unset;
  }
  img.mwe-math-fallback-image-inline {
    filter: invert(1);
  }
  table.wikitable,
  table.wikitable > tr > th,
  table.wikitable > * > tr > th,
  .mediawiki pre {
    background-color: rgba(255,255,255,0.1);
    color: unset;
  }
  tr, td, th, table, tbody, thead {
    border-color: #000 !important;
  }
  .wikiEditor-ui,
  .wikiEditor-ui-controls,
  .wikiEditor-ui-tabs,
  .wikiEditor-ui-buttons,
  .wikiEditor-ui-view,
  .wikiEditor-preview-contents,
  .wikiEditor-ui-top,
  .wikiEditor-ui-toolbar,
  .wikiEditor-ui .toolbar,
  .wikiEditor-ui .tool-select {
    background: unset !important;
    background-image: unset !important;
    background-color: unset !important;
    border: 0 none !important;
  }
  .wikiEditor-preview-contents,
  #wpTextbox1 {
    background-color: rgba(255,255,255,0.1) !important;
  }
  .wikiEditor-ui-toolbar {
    background-color: rgba(255,255,255,0.2) !important;
  }
  .wikiEditor-ui-tabs > div {
    background-color: rgba(255,255,255,0.1) !important;
    border-right: 1px solid #000;
    border-bottom: 0 none;
  }
  .wikiEditor-ui-tabs > .current {
    background-color: rgba(255,255,255,0.2) !important;
    border-bottom: 0 none !important;
  }
  [style*="background:"], [style*="background-color:"], [bgcolor] {
    background: rgba(255,255,255,0.1) !important;
    border: 0 none !important;
  }

  #CybotCookiebotDialog {
    border-top: 1px solid #d4931c !important;
  }
  #CybotCookiebotDialog img {
    background-color: transparent !important;
    filter: invert(1);
  }
  #CybotCookiebotDialog, #CybotCookiebotDialog :not(input):not(textarea):not(button):not(img):not(.CybotCookiebotDialogBodyButton) {
    color: #fff !important;
    background-color: #222 !important;
  }

}
