    color: #751483;
}
article {
    border: 1px solid #ffffff26;
    background-color: #101522b4;
    border-radius: 7px;
}
article {
	opacity: 1;
	animation-name: fadeInOpacity;
	animation-iteration-count: 1;
	animation-timing-function: ease-in;
	animation-duration: 0.5s;
}

@keyframes fadeInOpacity {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}
