@-moz-document url-prefix("https://duelingnexus.com/game") {
/*Theme Options*/
.engine-button-success {
    background-color: rgb(23, 15, 255);
}
.engine-button-primary {
    background-color: rgba(0,64,192,0.8);
}
/*Card Sleeve*/
.game-field-card[src="assets/images/cover.png"] {
	 content: url('https://vignette.wikia.nocookie.net/yugioh/images/f/fd/Back-Anime-ZX-2.png/revision/latest?cb=20140418110547');
}
/*Card Column*/
.engine-button {
    border: 0;
}
[class~=game-field-zone] {
    border: 1px solid #00000000;
    background-color: #00000080;
}
div#card-column {
    overflow: hidden;
    background-color: #00000080;
    box-shadow: 0px 200px #00000078;
    font-family: 'Antic', sans-serif;
    height: 100vh;
    width: 23vw;
    position: absolute;
    top: 0;
    left: 0;
}
img#card-picture {
    width: 19vw;
    max-width: 24vw;
    z-index:1;
    position:absolute;
    top: 0;
    left: 0;
    margin-bottom: 200%;
}
div#game-chat-area {
    transform: translate(218%, 9%) scale(0.7);
}
#card-id {
    display: none;
}
#card-name {
    font-size: 115%;
    text-align: left;
    background-color: #fff0;
    margin-top: 119%;
    word-spacing: -1px;
}
span#card-if-monster {
    display: inline-block;
    color: orange;
    word-spacing: -1px;
    font-size: 75%;
}
span#card-if-spell {
    color: green;
    display: inline-block;
    word-spacing: -1px;
    font-size: 75%;
}
span#card-description {
    display: inline-block;
    word-spacing: -1px;
    font-size: 75%;
}
#card-description-box {
    max-height: 20px;
    border: 0;
    background-color: #08080900;
    padding: 0;
    margin: 0;
    margin-top: -4%;
	top: 55vh;
    right: .4vw;
    left: .4vw;
}
#game-column {
    position: absolute;
    left: 25vw;
}
/*End message*/
div#game-end-window:hover {
    opacity: 0;
}
/*Siding fix*/
div#game-siding-column, div#game-siding-column-2 {
    transform: translateX(65%);
}
button#game-siding-done {
    margin-left: 30%;
    background-color: #3851b970 !important;
}
/*Field Remake*/
td {
    background: transparent !important;
}
td#game-field-opponent-spell1:hover, td#game-field-opponent-spell2:hover, td#game-field-opponent-spell3:hover, td#game-field-opponent-spell4:hover, td#game-field-opponent-spell5:hover, td#game-field-opponent-monster1:hover, td#game-field-opponent-monster2:hover, td#game-field-opponent-monster3:hover, td#game-field-opponent-monster4:hover, td#game-field-opponent-monster5:hover, td#game-field-player-spell1:hover, td#game-field-player-spell2:hover, td#game-field-player-spell3:hover, td#game-field-player-spell4:hover, td#game-field-player-spell5:hover, td#game-field-player-monster1:hover, td#game-field-player-monster2:hover, td#game-field-player-monster3:hover, td#game-field-player-monster4:hover, td#game-field-player-monster5:hover, td#game-field-extra-monster1:hover, td#game-field-extra-monster2:hover, td#game-field-player-spell6:hover, td#game-field-opponent-spell6:hover, td#game-field-opponent-graveyard:hover, td#game-field-opponent-banished:hover, td#game-field-player-graveyard:hover, td#game-field-player-banished:hover{
         background-color: #e60e0e00;
    -webkit-animation: glow2 0.55s infinite alternate;  
}
td#game-field-player-spell1, td#game-field-opponent-spell5 {
    background: url(https://i.imgur.com/vFIzlBE.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    border-left: 1px solid #fff !important;
}
td#game-field-player-spell5, td#game-field-opponent-spell1 {
    background: url(https://i.imgur.com/A280Jwa.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-player-spell6, td#game-field-opponent-spell6{
    background: url(https://i.imgur.com/P8Ef4DU.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-player-graveyard{
    background: url(https://i.imgur.com/id9Kkr9.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-opponent-graveyard::before{
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-transform: rotate(180deg);
transform: rotate(180deg);
z-index: -1;
    background: url(https://i.imgur.com/id9Kkr9.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-opponent-graveyard::before{
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-transform: rotate(180deg);
transform: rotate(180deg);
z-index: -1;
    background: url(https://i.imgur.com/id9Kkr9.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-player-banished, td#game-field-opponent-banished{
    background: url(https://i.imgur.com/gYYU27I.png) !important;
    background-size: 60% !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
}
td#game-field-player-spell5, td#game-field-player-spell4, td#game-field-player-spell3, td#game-field-player-spell2, td#game-field-player-spell1, td#game-field-player-graveyard, td#game-field-player-banished, td#game-field-opponent-banished, td#game-field-opponent-spell6, td#game-field-opponent-monster1, td#game-field-player-spell6, td#game-field-opponent-graveyard, td#game-field-opponent-monster5, td#game-field-opponent-monster3{
    transform: scalex(1.01);
    border-bottom: 1px solid white;
}
td#game-field-player-spell5, td#game-field-player-banished, td#game-field-opponent-spell6, td#game-field-opponent-spell1, td#game-field-extra-monster2, td#game-field-extra-monster1 {
    border-right: 1px solid white;
    transform: scalex(1.01);
}
td#game-field-opponent-spell5, td#game-field-opponent-banished, td#game-field-extra-monster1, td#game-field-extra-monster2,td#game-field-player-spell6 {
    border-left: 1px solid white;
    transform: scalex(1.01);
}
td#game-field-opponent-spell6, td#game-field-opponent-spell5, td#game-field-opponent-spell4, td#game-field-opponent-spell3, td#game-field-opponent-spell2, td#game-field-opponent-spell1, td#game-field-opponent-graveyard, td#game-field-opponent-banished, td#game-field-player-banished, td#game-field-player-graveyard, td#game-field-player-monster5,  td#game-field-player-monster1, td#game-field-player-spell6, td#game-field-player-monster3 {
    border-top: 1px solid white;
    width: 141% !important;
    transform: scalex(1.01);
}
div#game-end-window {
    margin-left: 49.5% !important;
}
#game-field {
    border-collapse: unset;
    border-spacing: 0 !important;
}
td#game-field-player-spell3 {
    transform: scalex(1.01);
}
/*Misc*/
.game-field-hand img {
    transition: transform .1s;
    vertical-align: 0%; 
}
td#game-field-player-hand > .game-field-zone-content > .game-field-card:hover {
    box-shadow: 0 0 20px #001aff;
}
.game-selectable-card {
    outline: 0;
    animation-name: fade2;
}
@keyframes fade2 {
  0% {
    opacity: 0.67
  }
  100% {
    opacity: 1;
  }
}
@-webkit-keyframes glow {
    to {
    -webkit-box-shadow: 0 0 2px #ff8400;
       -moz-box-shadow: 0 0 2px #ff8400;
            box-shadow: 0 0 2px #ff8400;
    }   
}
@-webkit-keyframes glow2 {
    to {
        background-color: #ffffff2b;
    }   
}
}
