css:
@-moz-document domain("barter.vg") {
body, header, #logolink, #bc, footer, #logolink, #signin {
	background-color: #161616;  
    color: white;
}

#main div:nth-child(2) {
    background: #161616 !important;
}

a {
	color: white !important;
}

#collection a, #offerHeaders a { 
	color: black !important;
}

#main > div:nth-child(2) > div > div:nth-child(6) a 
{
    color: black !important;
}

#offerForm, #collection {
	padding: 10px !important;
}

td{
	color: white;
}

table tr:hover td{
	background-color: #161616 !important;
}

#collection tr:hover td, #offerHeaders tr:hover td {
	background-color: white !important;
}

#offerHeaders td {
    color: black;
}

tr:hover .tag, .collection tr:hover ul, .collection tr:hover .showMoreLabel {
    color: #777;
}

aside {
    color: white !important;
}

.aside strong{
	color: white !important;
}

.navs:hover span, .navs:hover sup
{
    color: white !important; 
}

.navs abbr, h2.pborder {
    background-color: #00BFFF;
}

.pborder, .platform.pborder th {
    border-color: #00BFFF;
}

.pborder sup, .pborder span, #menu .pborder, #bc.pborder li:last-child:first-letter, #bc.pborder li:first-child:first-letter {
    color: #00BFFF;
}

#main > div.center {
 	border-bottom: .1em solid #00BFFF !important;   
}

#offers th {
    border-bottom: .08em solid #00BFFF;
    color: #00BFFF;
}

#offers .completed, .completed {
	color: #090 !important;
}

#offers, #offers .declined {
	color: #ED4337 !important;    
}

#offerHeader tr:hover td, #cards tr:hover td {
    background-color: #161616 !important;
}
#offerHeader tr:hover td, #cards tr:hover td {
    background-color: #161616 !important;
}
.tradables {
    border: .07em solid white !important;
}
#offerStatus {
    border-radius: 0px;
 	border: .07em solid white;   
}
.offerHistory:hover div {
	color: white;
}
.offerHistory strong {
    color: white;
}
.offerHistory span {
    background-color: #05f;
}
.statusCurrent {
	background-color: #00BFFF;
}
.statusCurrent span {
 	color: #00BFFF !important;   
}
.tradables_items_list {
    padding: 5px;
    padding-left: 10px;
}
#listEdit {
    padding: 5px;
    border: solid 1px #e50;
    background-color: #161616; 
}
.addFrom input {
    background-color: #161616 !important;
    color: white;
}
.notices, .highlight {
    color: black !important;
    padding: 5px;
}
.active {
    color: white;
}
}
