css:
@-moz-document domain("moo.do") {
#outerWrapper {
	background: #222!important
}
.collapser {
	color: #222!important
}
.fancyScrollContent {
	background: #333!important
}
.item:not(.overviewItem) .collapser:before {
	background: #333!important
}
.item:not(.overviewItem) .collapser:hover {
	background: #444!important;
	color: #000!important
}
.paneContent {
	background: #333!important
}
.paneHeader {
	background: #444!important;
	border-bottom: 1px solid #222!important
}
.paneOutline .item:hover {
	background: #444!important
}
.paneTopBox,
.paneOutline .item {
	background: #333!important;
	color: #000!important
}
.title {
	color: #111!important
}
html:not(.phone) .item:not(.overviewItem).hovered .collapser:before {
	background: #444!important
}
html:not(.phone) .searchWrap {
	background: #222!important;
	border: 1px solid #444!important
}
}
