@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fluidr.com") {

/* ==== Flickr WideScreen (Viewer) - FLUIDR v.4 (new4) ==== */

/* === FOR PREFERENCES - MEDIUM ALONE : fluid-size-z =================

- Item Display [ M ]
- Metadat Display : [ON ]
- Metadata Dimming : [OFF ]
- Increase Font Size : OFF ]
- Borders On Images : [ OFF ]

======================================================================= */

* {
  box-sizing: border-box;
}

body {
display: inline-block !important;
width: 100%!important;
max-width: 100%!important;
max-width: 1908px !important;
}
#photos {
display: inline-block !important;
width: 100%!important;
max-width: 100%!important;
max-width: 1908px !important;
/* outline: 1px solid red !important; */
}


#photos.fluid-size-l {
display: inline-block !important;
width: 100%!important;
max-width: 100%!important;
max-width: 1908px !important;
/* outline: 1px solid green !important; */
}
#photos.fluid-size-z {
display: inline-block !important;
width: 100%!important;
max-width: 100%!important;
min-width: 1908px !important;
max-width: 1908px !important;
/* outline: 1px solid yellow !important; */
}
#photos.fluid-size-m {
/* width: 100%!important; */
outline: 1px solid red !important;
}

/* PHOTOS CONTAINER */
#photos{
/* display: inline-block!important; */
display: table !important; 
width: 97%!important;
/* outline: 2px dotted red !important; */
}
/* PHOTOS - ROW  */
.photos-page {
/* display: table-row !important; */
/* display: table !important; */
/* display: inline-table !important; */
display: inline-block!important;
/* display: table-cell !important; */
width: 100% !important;
min-width: 1900px !important;
max-width: 1900px !important;
/* min-height: 500px !important; */
/* max-height: 500px !important; */
    margin-bottom: 10px !important;
/* outline: 2px dotted green !important; */
}

/* PHOTO PANEL - CELL */
.photo-panel {
/* display: table-row !important; */
/* display: table !important; */
/* display: inline-table !important; */
display: inline-block!important;
/* display: table-cell !important; */
max-height: 445px !important;
min-height: 445px !important;
/* height: 425px !important; */
min-width: 930px !important;
max-width: 930px !important;
margin: 0 auto !important;

/* top: 0px !important; */
margin-bottom: 5px !important;
margin-right: 6px !important;
padding: 5px 5px !important;
/* outline: 5px solid peru !important; */
/* background: tan !important; */
border: 1px solid gray !important;
}

/* (new4) */
.photoshow {
/* display: table-row !important; */
/* display: table !important; */
/* display: inline-table !important; */
display: inline-block!important;
/* display: table-cell !important; */
/* max-height: 100px !important; */
/* min-height: 100px !important; */
/* height: auto !important; */
height: 422px !important;

/* width: 200px !important; */
    min-width: 515px !important;
    max-width: 515px !important;
margin: auto !important;
/* background: red !important; */
/* outline: 1px solid tomato !important; */
}
.fluid-size-z .photo-subpanel-align {
/* display: inline-block!important; */
display: table-cell !important;
    min-width: 515px !important;
    max-width: 515px !important;
max-height: 430px !important;
min-height: 43px !important;
margin-bottom: 0px !important;
/* pointer-events: none !important; */
/* background: green !important; */
}
.photo-subpanel-align {
/* display: table-row !important; */

/* display: inline-table !important; */
/* display: inline-block!important; */
/* display: table-cell !important; */
/* height: 200px !important; */
    margin-bottom: 2em;
    text-align: center;
    vertical-align: top;
}

/* (new2) IMAGE CONTAINER - === */

.photo-subpanel a {
display: inline-block !important;
/* height: 100px !important; */
    color: #aaa;
    cursor: pointer;
    text-decoration: none;
    transition: color 0.5s ease 0s;
/* pointer-events: none !important; */
/* z-index: 100 !important; */
/* background: red !important; */
}

.photo-panel-img {
/* display: table-cell !important; */
/* display: table-row !important; */
/* display: table !important; */
/* display: inline-table !important; */
display: inline-block!important;

/* max-height: 100px !important; */
/* min-height: 100px !important; */
/* height: auto !important; */
/* width: 200px !important; */
/* margin: auto !important; */
background-position: 50% 50% !important;
     background-size: 90% auto; 
/* pointer-events: none !important; */
/* outline: 1px solid peru !important; */
}

/* (new4) IMAGE / VIDEO CONTAINER - FADER ZOOM - FOR THUMBNAIL ZOOM - === */
.photo-panel-img .fader, 
.photo-panel-video .fader {
display: none !important;
max-height: 0px !important;
min-height: 00px !important;
z-index: 100 !important;
/* background-color: blue !important; */
outline: 1px solid violet !important;
}
.photo-panel .photo-panel-img {
display: inline-block!important;
max-height: 424px !important;
min-height: 424px !important;
    min-width: 515px !important;
    max-width: 515px !important;
/*     cursor: pointer !important; */
cursor: zoom-in !important;
/* pointer-events: none !important; */
background-size: contain !important;
}
img.zoomed-element {
/* cursor: url("./img/zoomout.cur"), pointer !important; */
cursor: zoom-out !important;
}

.meta-subpanel-align {
/* position: absolute; */
/* display: inline-block!important; */
display: table-cell !important;
max-height: 420px !important;
min-height: 420px !important;
/* width: 285px!important; */
/* vertical-align: middle; */
/* left: -500px; */
/* padding-bottom: 10px; */
/* padding-left: 10px; */
/* padding-top: 10px; */
    padding: 0px 5px !important;
/* transition: opacity 0.5s ease 0s; */
/* outline: 1px solid peru !important; */
}


/* (new4) */
.meta-subpanel {
display: inline-block!important;
max-height: 420px !important;
min-height: 420px !important;
/* text-align: left; */
/* width: 285px!important; */
/* word-wrap: break-word; */
overflow: hidden !important;
/* outline: 1px solid red !important; */
}
.meta-elem-actions {
    margin-top: 8px;
    height: 345px !important;
    border-bottom: 1px dotted rgba(78, 78, 78, 0.84) !important;
}

.meta-subpanel .scrollbar {
    background: transparent none repeat scroll 0 0;
    float: right;
    position: relative;
    width: 10px;
    height: 309px !important;
}
.meta-subpanel .viewport {
    overflow: hidden !important;
    position: relative;
    height: 311px !important;
/* background: tan !important; */
}
/* .meta-subpanel .photo-tags */
/* .meta-subpanel .photo-desc {
    color: red !important;
    font-size: 0.9em;
    margin-top: 0;
    width: 390px;
    height: 422px !important;
    word-wrap: break-word;
} */


/* PHOTO - LARGE - METADATA ON === */
#photos.fluid-size-l .photos-page [id^="photo-panel-"] {
/* outline: 1px solid green !important; */
}

/* PHOTO - MEDIUM - METADATA ON === */
#photos.fluid-size-z .photos-page [id^="photo-panel-"] {
/* outline: 1px solid yellow !important; */
}
/* PHOTO - SMALL - METADATA ON === */
#photos.fluid-size-m .photos-page [id^="photo-panel-"] {
/* outline: 1px solid red !important; */
}

/* PHOTO - LARGE - METADATA OFF === */
#photos.fluid-size-l-meta-hide .photos-page [id^="photo-panel-"] {
/* outline: 1px dashed green !important; */
}

/* PHOTO - MEDIUM - METADATA OFF === */
#photos.fluid-size-z-meta-hide .photos-page [id^="photo-panel-"] {
/* outline: 1px dashed yellow !important; */
}
#photos.fluid-size-z-meta-hide .photos-page [id^="photo-panel-"] {
    max-height: 500px !important;
    min-height: 500px !important;
    max-width: 530px !important;
    min-width: 530px !important;
}

/* (new4) PHOTO - SMALL - METADATA OFF === */
#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] {
/* outline: 1px dashed red !important; */
}

#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] {
    max-height: 400px !important;
    min-height: 400px !important;
    max-width: 350px !important;
    min-width: 350px !important;
margin-bottom: 50px !important;
margin-right: 15px !important;
/* border: 1px solid violet !important; */
/* background: yellow !important; */
}
#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] .photo-subpanel-align {
    max-height: 300px !important;
    min-height: 300px !important;
    max-width: 330px !important;
    min-width: 330px !important;
/* margin-bottom: 40px !important; */
/* border: 1px solid red !important; */
/* background: green !important; */
}
#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] .photo-subpanel  {
    max-height: 300px !important;
    min-height: 300px !important;
    max-width: 330px !important;
    min-width: 330px !important;
} 
#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] .photo-subpanel a  {
    max-height: 300px !important;
    min-height: 300px !important;
    max-width: 330px !important;
    min-width: 330px !important;
} 
#photos.fluid-size-m-meta-hide .photos-page [id^="photo-panel-"] .photo-panel-img {
    max-height: 300px !important;
    min-height: 300px !important;
    max-width: 330px !important;
    min-width: 330px !important;
}
/* === FOR PREFERENCES - SMALL ALONE : fluid-size-m-meta-hide =======
- Item Display [ S ]
- Metadat Display : [OFF ]
- Metadata Dimming : [OFF ]
