.argmore a strong {
	display: inline-block;
	border-top: 1px solid #ce5513;
	background: #ce5513;
	background: -webkit-gradient(linear, left top, left bottom, from(#ce5513), to(#ce5513));
	background: -moz-linear-gradient(top, #ce5513, #ce5513);
	background: -o-linear-gradient(top, #ce5513, #ce5513);
	padding: 1px 10px;
	-webkit-border-radius: 8px;
	-moz-border-radius: 8px;
  -o-border-radius: 8px;
	border-radius: 8px;
	-webkit-box-shadow: rgba(0,0,0,1) 0 1px 0;
	-moz-box-shadow: rgba(0,0,0,1) 0 1px 0;
  -o-box-shadow: rgba(0,0,0,1) 0 1px 0;
	box-shadow: rgba(0,0,0,1) 0 1px 0;
	text-shadow: rgba(0,0,0,.4) 0 1px 0;
	vertical-align: middle;
}
.argmore a:hover strong {
	background: #d9733c;
	background: -webkit-gradient(linear, left top, left bottom, from(#d9733c), to(#d9733c));
	background: -moz-linear-gradient(top, #d9733c, #d9733c);
