/*El color de cabecerá*/	
	#blackbar {
	    background: #003366;	    
	}

	body {
	    background: url(http://orig05.deviantart.net/13c9/f/2017/028/c/c/background_charizard_skin_by_narenjr17-dax3icp.jpg) no-repeat top center fixed;
	}

/*Menu VB pasar a otras paginas*/
	.vbmenu_control {  
        background: #003366;
	    color: #FFFFFF;
	    font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	    padding: 3px 6px 3px 6px;
	    white-space: nowrap;
                                        }

	#controlbar,#collapseobj_controlbar {
	    background: rgba(0, 0, 0, 0.2);
	    height: 39px;
	    padding: 0 !important;
	    overflow: visible;
	}

/*Quitar la fea sombra de degradado de la skin original*/
	#forocielo {    
	    background: none;
	    margin-bottom: -80px;
	    position: absolute;
	    width: 100%;
	    z-index: -1;
	}

/*Echar el cuerpo hacia abajo con el height*/
	#cabecera {
	    height: 220px;
	    position: relative;
	}

/*Imagen de Whack a hack foro, en el Index*/
	.forumhome #cabecera h1 {	    	    
	    background: transparent url(http://orig01.deviantart.net/23f0/f/2017/028/c/3/charizardx_logo_by_narenjr17-dax32h2.png) no-repeat scroll center 20px;
	    width: 400px;
	    height: 180px;
	    bottom: 0px;
	    left: 0px;
	    box-shadow: none;
	}

	/*Texto colores*/	
	a:link,	body_alink {
	    color: #0066cc;
	}

/*Color links visitados*/
	a:visited, body_avisited {
	    color: #004080;
	}

 /*Color mouse sobre link*/
	a:hover, a:active, body_ahover {
        color: #00264d;
	    text-decoration: underline;
	}

	.new_float_button a {
	    display: inline-block;
	    background: rgba(0, 0, 0, 0.4);
	    color: #FFF;
	    padding: 5px 3px;
	    min-width: 72px;
	    text-align: center;
	    box-sizing: border-box;
	    border-bottom-left-radius: 6px;
	    border-bottom-right-radius: 6px;
	    text-decoration: none;
	    transition: .3s;
	}

	/*Extra colores*/
	.thead {
	    background: #003366;	    	    
	    color: #FFFFFF;
	    font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    }

	/*Zona de botones*/	
	a.mini_button {
	    background-color: #0059b3;
	    height: 17px;
	    font-size: 10px;
	    border: medium none;
	    padding: 4px 6px 4px;
	    color: #FFF;
	    border-radius: 4px;
	    transition: .3s;
	    text-decoration: none;
	}
	a.mini_button img {
	    margin-left: -6px;
	    margin-right: -40px;
	    padding-left: 6px;
	    padding-right: 40px;
	    box-sizing: content-box;
	    padding-bottom: 20px;
	    position: absolute;
	    vertical-align: top;
	}
	a.mini_button:hover {
	    background-color: #0073e6;
	    color: white;
	}
	.mini_button.off::after {
	    content: "Cita +";
	}
	.mini_button.on::after {
	    content: "Cita -";
	}
	.forum_tablon {
	    background: rgba(0, 0, 0, 0.4)
	}
	.forum_tablon a,
	.forum_tablon span {
	    color: #FFF !important
	}
	.forum_tablon tr,
	.forum_tablon td,
	.forum_tablon .tcat,
	.forum_tablon .thead {
	    background: transparent !important;
	    color: #FFF !important
	}
	.button.white,
	.button.white:link,
	.button.white:visited {
	    color: #0059b3;
	    background-color: transparent !important;
	    box-shadow: none !important;
	    padding: 3px 6px 5px;
	}
	.button.white .ripple-effect {
	    background: #0059b3;
	}
	.button.closed {
	    background: #0059b3;
	    padding-left: 27px;
	}
	.button,
	.button:link,
	.button:visited {
	    -webkit-box-sizing: border-box;
	    -moz-box-sizing: border-box;
	    box-sizing: border-box;
	    min-width: 65px;
	    text-align: center;
	    vertical-align: middle;
	    display: inline-block;
	    font-size: 12px;
	    text-transform: uppercase;
	    padding: 5px 10px;
	    cursor: pointer;
	    border-radius: 4px;
	    /* box-shadow: 0 2px 5px rgba(0,0,0,.26); */
	    
	    -webkit-user-select: none;
	    user-select: none;
	    position: relative;
	    overflow: hidden;
	    outline: none !important;
	    transition: .3s;
	    -webkit-tap-highlight-color: transparent;
	    font-weight: normal;
	    border: 0 none !important;
	    background: #0059b3;
	    color: #EEEEEE;
	    text-shadow: none !important
	}
	.button:focus,
	.button:hover {
	    text-decoration: none;
	    color: #FFF;
	    background-color: #0073e6;
	    /* box-shadow: 0 3px 6px rgba(0,0,0,.33) */
	}

