/* startseite */
body {
	background-color : #0e0e0e; /* #f4eee8; */
	color            : #FFF; /* nicht definiert */
}
input {
	background-color : #000;
	color            : #FFF;
	border           : 1px solid #C0C0C0;
	border-radius    : 2px;
}
input:hover ,
input:focus {
	background-color : #222;
	border-color     : #AAA;
}
input[type="submit"] {
	cursor : pointer;
}
a {
	color : #88f; /* #44a */
}
a.link_ext, a.link_int {
	color : #88f; /* #44a */
}
div#footer div.footlinks {
	background-image : none;
	color            : #474747;
}
div#footer div.footlinks a:hover {
	color: #FFF; /* black */
}
div#footshadowleft {
	background-image : none;
}
div#root {
	background-color : #000; /* white */
	border-color     : #333; /* #d8d0cc */
}
div#content {
	background-color : black; /* white */
	border-color     : #333; /* #ccc */
	color            : #e2e2e2; /* #444 */
}
div#contentheading {
	background-color : #333; /* #ccc */
}
div#wrapper {
	background-color: #333; /* #ccc */
}
div#contentheading h1 {
	color  : #e2e2e2; /* #444 */
}
div#headerback {
	filter       : grayscale(100%) invert(100%);
	border-color : #FFF; /* invertiert ergibt es #000 */
}
div#mainnavigation {
	text-shadow : 1px 1px 1px black;
}

/* der mensch - sprache - http://www.gebaerdenlernen.de/index.php?article_id=26 */
div.culture {
	background-color : #1e1d18; /* #fff8ce; */
}
div.history {
	background-color : #29251e; /* #ffe7ba */
}
table.entrytable td {
	border-color : #333; /* #ddd */
}
table.entrytable a {
	color: #88f; /* #448 */
}
table.entrytable tr:hover {
	background-color: #222; /* #f8f8f8 */
}
div.download, div.link {
	background-color: #333; /* #eee */
}
div.download a, div.link a {
	color: #88f;; /* #44a */
}
div.download a:hover , div.link a:hover {
	color : #bfbfff; /* #88f; */
}
/* wörterbuch - übungen - fingeralphabet - stufe 1 - http://www.gebaerdenlernen.de/index.php?article_id=112 */
div.question {
	background-color : #333; /* #eee */
}
div.answerblock {
	background-color : #333; /* #eee */
}
div.rightanswer {
	background-color: #304c22; /* #dfc */
}
div.wronganswer {
	background-color : #4e2511; /* #fdc */
}
/* wörterbuch - übungen - fingeralphabet - stufe 4 - http://www.gebaerdenlernen.de/index.php?article_id=109 */
a.videolink {
	background-color : #45454c; /* #eef */
	border-color     : #779; /* #779 */
	border-radius    : 2px;
	text-shadow      : 1px 1px 1px black;
}
/* Wörterbuch A-C - http://www.gebaerdenlernen.de/index.php?article_id=87 */
div#content p.buchstabe {
	background-color : #444;
	color            : #EEE;
	border-color     : #666;
}
/* Was steht oder bewegt sich hier ? */
div.answerblock td img {
	border-color : black;
}
/* auswertung */
div.summary {
	background-color : #333; /* #eef */
}
div.summarydetail {
	background-color : #333; /* #eef */
}
div.summary span.number {
	color : #FFF; /* black */
}
div.summarydetail a:hover {
    color       : #a3a3e6; /* #008 */
    text-shadow : 1px 1px 1px #000;
}
/* I DGS für Mediziner - Erste Begegnung - http://www.gebaerdenlernen.de/index.php?article_id=83 */
div.grammatik {
	background-color : #2a3126; /* #dfc */
}
div.wortschatz {
	background-color : #2c2833; /* #dcf */
}
/* Haus und Garage - http://www.gebaerdenlernen.de/index.php?article_id=62 */
div.example {
	background-color : #2a3126; /* #dfc */
}
div#content p.dictsection {
	background-color : #444; /* #eee; */
	color            : #CCC; /* #888 */
	border-color     : #888; /* #CCC */
}
div#content p.dictsection b {
	color : #FFF; /* #222; */
}
}

@-moz-document domain("gebaerdenlernen.de") {
/* alles was das video overlay aka den player betrifft */
div#playerframe a.close {
	background-color : #FFF;
	border-radius    : 18px;
}
div#playerframe a.close img {
    padding        : 2px;
    padding-bottom : 0px;
}
div#playerframe {
    border-color : #555; /* #888 */
}
/* jp player - http://www.jplayer.org */
.jp-audio, .jp-audio-stream, .jp-video {
	border-color     : #002637; /* #009be3 */
	background-color : #1a1a1a; /* #eee */
}
.jp-video .jp-interface {
	border-top : 1px solid #002637; /* #009be3 */
}
.jp-interface {
	background-color : #111; /* #eee */
}
.jp-progress {
	background-color : #333;
}
.jp-video .jp-current-time ,
.jp-video .jp-duration {
	color : #eee;
}
.jp-volume-controls button ,
.jp-toggles button ,
.jp-controls button ,
.jp-volume-bar-value ,
.jp-seek-bar {
	filter : invert(100%); /* grayscale(100%);*/
}

}
