css:
@-moz-document domain("www.wunderlist.com") {
body,
#lists,
#tasks,
#add-taks,
.sidebarFolder.expanded,
.sidebarActions,
#detail .inner .body,
#detail .inner .top {
    background-color: #444444
}
#wunderlist-base,
.sidebarItem a .title,
.taskItem-duedate,
.taskItem-titleWrapper,
#user-toolbar .user-name,
.sidebarActions-addList,
.subtask .section-title,
.content-fakable .display-view,
.count,
#wunderlist-base textarea,
#wunderlist-base:focus,
.content-fakable textarea, .content-fakable pre {
    color: #FFF !important
}
#fullscreen-note .content,
.dialog .notice,
.dialog .content,
#wunderlist-base [type=text].big,
#wunderlist-base [type=email].big,
#wunderlist-base [type=password].big,
#wunderlist-base .input-fake.big,
.dialog .content-header,
#wunderlist-base [type=text].hasIcon,
#wunderlist-base [type=email].hasIcon,
#wunderlist-base [type=password].hasIcon,
#wunderlist-base .input-fake.hasIcon {
    color: #000
}
.sidebarActions-addList,
#wunderlist-base .comments-add .input-fake,
.background-12#wunderlist-base .addTask {
    background: rgba(255, 255, 255, 0.05)
}
.checkBox .detail-check,
.checkBox .task-check,
.checkBox .detail-checked,
.checkBox .task-checked {
    stroke: rgba(255, 255, 255, 0.8);
    fill: white
}
.background-12 #list-toolbar,
.background-12 #search-toolbar,
#detail .title-container,
.sidebarItem.active a,
body,
.taskItem.selected .taskItem-body,
.foldersGuide .openButton,
#wunderlist-base .comments-bottom,
#detail .bottom {
    background-color: #222244
}
.taskItem-body {
    background-color: #555555;
}
#wunderlist-base #detail .section-item .section-title {
    color: #AAA
}
#wunderlist-base #detail .section-item.date .section-title,
#wunderlist-base #detail .section-item.assigned .section-title {
    color: #FFF
}
#wunderlist-base #detail .section-item.date .date,
#wunderlist-base #detail .section-item.assigned .date,
#wunderlist-base #detail .section-item.date .assigned,
#wunderlist-base #detail .section-item.assigned .assigned,
#wunderlist-base #detail .section-item.date .reminder,
#wunderlist-base #detail .section-item.assigned .reminder,
.bottom {
    fill: #FFF
}

.taskItem.active .taskItem-body,
.taskItem.selected .taskItem-body,
.sidebarItem.active a,
.sidebarFolder.active {
  box-shadow: inset 0 0 0 2px #AED9FF;
}
}
