css:
/* -- triplc 20170213 -- */

@-moz-document domain("unseen.is") {

.user-manager-info .img-circle {border-radius:30%!important}
.chat-list li.left, .chat-list li.right {padding:4px 8px!important}
.chat-list .chatting-avatar {margin:0!important;float:left!important}
.chat-list .chatting-avatar .img-circle {border:none!important}
.chat-list .chatting-user {margin:0 8px 0 56px!important;padding:0!important;border:0!important;border-radius:0!important;background:none!important}
.chat-list h5.name, .chat-list .message-area {margin:0!important;padding:0!important}
.chat-list .message-row, .chat-list message, .chat-list .message, .chat-list .message-content {margin:0!important;padding:0!important;line-height:1em!important;min-height:0!important;border:none!important;border-radius:0!important}
.message-row .file {padding:4px 8px 1px 8px;margin:4px 70px 4px 0!important;border-radius:0!important;min-height:32px!important}
.hspliter {background:none!important;background-color:rgba(0,0,0,0)!important;color:rgba(0,0,0,0)!important}
.scrollbarX, .scrollbarY, .scrollContent, .myScroll {background:none!important}
.thumb {background-color:#888!important;border-radius:0!important;border-width:0!important}
body.cke_editable {margin:2px 4px!important}

.btn-primary {border-radius:0!important}
.btn-success, .btn-cancel {border-width:1px!important;border-radius:0!important;border-color:#444444!important;padding:2px 8px!important;margin:0 2px 2px 2px!important}
.preview .thumbnail {border-radius:0!important}

}
