/* -- triplc 20170213 -- */

@-moz-document domain("unseen.is") {

#app, body.cke_editable {background-color:#000!important;color:#ddd!important}
#app div {background-color:#000!important;color:#ddd!important;background-image:none!important}

a, a:focus, a:link, a:visited {background:none!important;color:#fff!important}
a:hover, a:active {background:none!important;color:#fa8!important}

#unseen-aside-panel, #unseen-aside-panel div,
#unseen-tab-content, #unseen-tab-content div,
#unseen-main-chat-ul, #unseen-main-chat-ul div {background-color:#1a1a1a!important}

#user-manager, #unseen-user-menu,
	.collapse-menu,
	.contact-title {background:none!important}

#search-input-text {background-color:#000000!important}

.btn-primary {border:0!important;border-radius:0!important}
.btn-primary:focus,.btn-primary:link, .btn-primary:visited,
.btn-primary {background-color:#444444!important}
.btn-primary:hover, .btn-primary:active {background-color:#6078bf!important}

.btn-success, .btn-cancel {border-width:1px!important;border-radius:0!important;padding:2px 8px!important;margin:4px!important;}
.btn-success:focus, .btn-success:link, .btn-success:visited, .btn-success {border-color:#666!important;background:#333!important;color:#ddd!important;}
.btn-cancel:focus, .btn-cancel:link, .btn-cancel:visited, .btn-cancel {border-color:#333!important;background:#1a1a1a!important;color:#aaa!important;}
.btn-success:hover, .btn-success:active, .btn-cancel:hover, .btn-cancel:active {background-color:#6078bf!important;border-color:#6078bf!important;color:#fff!important;}

ul#user-menu {background-color:#1a1a1a!important}
ul#user-menu li a:focus, ul#user-menu li a:link, .menuTab:visited,
ul#user-menu li a {background-color:#444444!important;border:0!important}
ul#user-menu li a:hover, ul#user-menu li a:active {background-color:#6078bf!important;border:0!important}

.message-row .file {border-radius:0!important;border-color:#666!important}
.preview .thumbnail {border-radius:0!important;border-color:#888!important}

ul.recent-list-ul>li, ul.contact-list-ul>li, ul.group-list-ul>li {background:none!important;border-width:0 0 0 2px!important}
ul.recent-list-ul>li.active, ul.contact-list-ul>li.active, ul.group-list-ul>li.active {background:none!important;border-left-color:#fa4!important}

#unseen-main-chat-ul li a {background-color:#1a1a1a!important}

#group-chat {background-color:#000!important;border:0!important}

li.activeChat.selected>a {color:#8f8!important;font-weight:bold!important}
li.activeChat.selected>a:hover, li.activeChat.selected>a:active {color:#fa8!important}
.x-close {background:#1a1a1a!important}

.pull-left a {background-color:#444!important}
.pull-left a:hover, .pull-left a:active {color:#fff!important;background-color:#6078bf!important}
.pull-right a {background-color:#888!important}
.pull-right a:hover, .pull-right a:active {color:#fff!important;background-color:#6078bf!important}

.timestamp {color:#ade!important}
.timestamp {border-bottom-color:#444!important}
.timeText {background:#000!important;color:#ade!important}
h5.name {color:#8ea!important}
.message-content {color:#ddd!important}

.activeChat, .groupchatTab {border-color:#888!important;}
.chat-area .chat-list-wrap {border-color:#888!important;}
.input-area .status-area {border-color:#888!important;}
.hsplitter {background:none!important;background-color:rgba(255,255,255,0)!important}
.cke_chrome, .cke_browser_gecko {border:none!important}
.options {border:0!important}
.options>button:hover, .options>button:active {color:#fa8!important}
.chat-data-channel-status {display:none!important}

#unseen-user .popover {background-color:#fff!important}

}
