css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mediatize.info") {

    html, body {
        background-image: url(data:image/png;base64,)!important;

        background-repeat: repeat !important;
        background-color: #181818 !important;
    }

    .header-top-wrapper {

        background-color: #e35614;

       background-image: url(data:image/png;base64,) !important;
        background-blend-mode: difference;
    }

    body {

        color: darkgray;
    }

    a {

        color: darksalmon;
    }
    
    .edit-btn.comment-btn {
	color: white;
	
}
      .edit-btn.comment-btn:hover {
	background: transparent;
}
    #wrap {
        background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
    }
    
    div.sidebox {
        background-color: transparent;
    }
    
    .sidebox .votes, .sidebox .clics {
        background-color: transparent;
    }
    .header-menu-wrapper {
        background-color: #444;
        border-bottom: 1px solid #444;
    }
    .sidebox .body {
        border-bottom: 1px solid black;
        border-left: 1px solid black;
        border-right: 1px solid black;
        border-bottom-left-radius: 8px;
        border-bottom-right-radius: 8px;
        padding-top: 10px;
    }

    .sidebox .cell {
        border-bottom: 1px solid black;

    }
    .sidebox.brown .header {
        background: #202020;
    }
    .sidebox .header {
        background: #202020;
        border-right: 1px solid black;
        border-top: 1px solid black;
        border-left: 1px solid black;
    }
.sbox a.numcop {
	color:white;
    background: transparent;
}
    .sbox a.numcop:hover {
	color:darksalmon;
        opacity: 1;
    background: transparent;
}
    #newswrap {
        background-color: transparent;
    }
    
    .news-body h1 a, .news-body h2 a {
        color: lightgray;
    }
    .news-content {
        color: lightgray;
    }
    .news-content2 {
        color: lightgray;
    }
    
    .news-content:hover {
        color:white;
    }
    
    #toplink h1 a, .news-body h1 a, .news-body h2 a, .news-info h1 a, .news-info h2 a {
        color:lightgray;
    }
    
    .share-icons {
        color:white;
    }
    
    a.comment-bubble {
        color:white;
    }
    
    .news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
        border-color: #444;
        background-color: black;
    }
    .news-details a.comments {
        background-color: transparent;
    }
    .news-details , .news-details .news-details-main{

        color: darkcyan;
        border-top: none !important;
        border-bottom:  none !important;

    }

    button.link.favorite {
        border-right: none;
    }

    .news-details .tool {

        border-left: none;

    }
    .news-details button.social-share {
        border-right: none;
    }

    .news-details form {

        border-left: none;
    }

    .news-body .box {

        border-color: #333;
        background: #444;

    }

    .temas {
        background-color:#444;
    }
    #header-center {
        color:white;
        background:#555;
    }
    #header-menu ul.menu01-itemsl li a {
	color: lightgray;
}
    .subheader {
        color:white;
        background: #555;
    }
    
    .subheader li a {
        color:lightgreen;
    }
    
    a.header-btn {
        color:#555555;
        background-color:#000000;
    }
    #header-center ul.menu01-itemsr {
        background-color:transparent;
    }
    #header-center ul.menu01-itemsr li a {
       color:cyan;
    }
    .header-menu01 ul li a {
        color: lightgray;
    }
    a.menu-more-button {
        color: lightgray;
    }

    .dropdown-menu {

        background-color: #555;
    }

    
    .header-menu01 a.submit_new_post {
        text-transform: uppercase;
        padding: 4px 10px;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
        border-radius: 3px;
        color: #e35614;
        margin-top: -6px;
        font-size: 12px;
        font-weight: 400;
        background-color: transparent;
        background-image: none;
        border: 0;
        box-shadow: none;
    }
    
    
    select.options-comments, select {
        background: #222;
        color: darkgray;
        border: none;
    }
    .form-control , input, textarea {
        background: #222;
        border: 1px solid black;
        color: gray;
    }


    legend, legend a, legend a:visited {
        border:none; 
    }
    .news-shakeit .votes {

        border: none;

    }
    .story-blog .main-content .text {
        font-family: 'Open Sans', 'Helvetica Neue', Helvetica, Arial;
        font-size: 16px;
        letter-spacing: 0;
        line-height: 22px;
        text-align: justify;
        color: #666;
    }
    .news-summary .warn, .story-blog .warn {

        background-color: #333;
    }
    .news-shakeit .votes, .news-shakeit .clics {
        background-color: #333;
    }

    .news-shakeit .clics {

        border: none;
    }
    .news-shakeit .votes, .news-shakeit .clics {
        background-color: #333;
    }
    #toplink h1 a:hover, .news-body h1 a:hover, .news-body h2 a:hover, .news-info h1 a:hover, .news-info h2 a:hover {
	color: darksalmon;
	border-color: darksalmon;
	text-decoration: none;
}
    #toplink, .sidebox-sub, .sneaker-legend, .pages a , .pages span,.topbox , .comment-body , fieldset {
        border: 1px solid rgba(0,0,0,.7) !important;
        border-top-color: rgba(255,255,255,.12) !important;
        border-radius: 3px !important;
        background: #333;
        box-shadow: 0px 0px 4px 0px #000 !important;

        color:gray;
    }
    #toplink p {
        color: white;
    }
    .story-blog .main-content h1 a, .story-blog .main-content h1 a:visited, #toplink h1 a {
        color: darksalmon;

    }
    .story-blog .best-comments .item .author a, .story-blog .related .item .author a, .story-blog .best-comments .item .text .tooltip, .story-blog .best-comments .item .text {

        color: #555;

    }
    
    .best-comment {
	background: transparent;
}
    .comment-body.hidden {
	background:transparent;
}

.comment-body.admin {
        color:lightgrey;
	background:grey;
}

    .genericform {

        background: transparent;

    }

.sidebox .votes span {
	color:lightgray;
}
    
    .sidebox .comments li {
        border-top: none;
	color:grey;
}

    .sidebox-rounded .body {
        border-bottom: 1px solid black;
        border-left: 1px solid black;
        border-right: 1px solid black;
    }

    .sidebox-rounded div.finished {

        background-color: black;
    }
    .sneaker-item {
        border-bottom-color: #444;
    }
    a, a:focus, a:hover, a:visited, .comment-info a  {

        color: darksalmon;
    }     
    .sneaker {

        background: none;
    }
    .favorite {
        display: inline-block;
        border-radius: 0px;
        border: none;
        margin: 0 4px;
        cursor: pointer;
        height: 18px;
        width: 16px;
        background: #111 url(https://mnmstatic.net/v_95/img/common/icons-common.png) no-repeat;
        background-size: 20px 300px;
        background-position: 0px -91px;
    }

    .comment-body.author {
        background-color: #444;
    }

    .comment-body.phantom {

        color: #444;
        background: #222;
    }

    .threader {
        border-left: none;

    }

    .pages {
	background: transparent;
}
    .pages span.nextprev {
        color: black;
    }

    #footcol1 {

        background: none;

    }
    #footcol1, #footcol2, #footcol3, #footcol4, #footcol5 {
        border-left:  none;
    }

    
    .news-shakeit.mnm-discarded .votes, .news-shakeit.mnm-discarded .clics {
        background-color: #555;
    }
    .comment-body .tooltip {
        color: darkcyan;
    }
    .comment-body.high {
        background: #432 url(https://www.mediatize.info/v_25/img/common/comment-high-01.png) 99% 5px no-repeat;
        background-blend-mode: overlay;
    }
    .comment-body.high.author {
        background: #542 url(https://www.mediatize.info/v_25/img/common/comment-high-01.png) 99% 5px no-repeat;
        background-blend-mode: overlay;
    }
    
    .postit.comment-body {
        background-color:#333;
    }
    
}
