css:
@-moz-document url-prefix("https://nzbindex.com"), url-prefix("http://nzbindex.com") {
body {
    background-color: black;
}
.incomplete {
    color: #d33682;
}
.niftycorners {
    visibility: hidden;
}
#termcloud > span > a {
    color: #b58900 !important;
}
td[colspan="5"][class="even"],
[colspan="5"][class="odd"] {
    filter: opacity(0.25);
    -webkit-filter: opacity(0.25)
}
#languages ul {
    filter: opacity(0.5);
    -webkit-filter: opacity(0.5);
}
#glass > a > img {
    visibility: hidden;
}
#menu > ul > li > a > span {
    visibility: hidden;
}
#menu > ul {
    filter: opacity(0.5);
    -webkit-filter: opacity(0.5);
    margin-top: 18px;
}
#header {
    background-color: #073642;
    background-image: none;
    margin-bottom: 0px;
    height: 41px;
}
#header .corner {
    visibility: hidden;
}
#header #logo {
    filter: opacity(0.5);
    -webkit-filter: opacity(0.5);
    top: 36px;
    left: 26px;
}
#main > div:nth-child(3) > center {
    visibility: collapse;
    height: 0;
}
#results > form > table > tbody > tr:nth-child(2) > td > div {
    visibility: collapse;
    height: 0;
}
#search {
    margin-bottom: 0;
    margin: auto;
    width: 730px;
}
#main {
    background: #002a35;
}
#container {
    background: #002a35;
}
#results tbody tr.odd {
    background-color: #002a35;
    color: #93a1a1;
}
#results tbody tr.even {
    color: #93a1a1;
}
#main {
    color: #93a1a1;
    background-color: #073642;
    background: #073642;
}
#results > form > table > thead > tr:nth-child(1) > td:nth-child(1) > a {
    visibility: hidden;
}
#results a.group {
    text-decoration: none;
    color: #93a1a1;
}
fieldset.searchform {
    background-color: #073642;
    margin: 0;
}
ul.grouplist {
    background-color: #073642;
}
a:link,
a:visited {
    color: #b58900;
}
.sizeblock {
    visibility: hidden;
}
select,
input {
    background-color: #002b36;
    color: #93a1a1;
    border-color: #002b36;
}
#bottom {
    visibility: hidden;
    height: 0;
}
#helpicon {
    visibility: hidden;
}
tr.odd:hover {
    background-color: #002a35 !important;
}
tr.even:hover {
    background-color: #073642 !important;
}
input[type="checkbox"] {
    background: red !important;
    color: blue;
}
}
