css:
@-moz-document domain("forums.overclockers.co.uk") {
#content {
    padding-top: 20px;
}
.messageList > div {
    margin-top: 30px;
}
.pageNavLinkGroup {
    padding: 10px;
}
.pageWidth {
    width: 98%;
    margin: 0 auto;
}
.discussionListItem {
    padding-top: 5px;
    padding-bottom: 5px;
}
.message .privateControls .item:last-child {
    margin-right: 5px;
}
#navigation {
    margin-top: 20px;
}
.messageUserInfo {
    border-top: 0px solid rgb(0, 0, 0);
    border-right: 0px solid rgb(0, 0, 0);
    border-left: 0px solid rgb(0, 0, 0);
    border-bottom: 0px solid rgb(0, 0, 0);
    box-shadow: 10px 10px 24px -5px rgba(0, 0, 0, 0.0);
}
body[contenteditable="true"] {
    background: white;
    color: black !important;
}
body[contenteditable="true"] a:link,
body[contenteditable="true"] a:visited,
body[contenteditable="true"] a:focus,
body[contenteditable="true"] a:active {
    color: black !important;
}
input[name="node_ids[]"] {
    float: right;
}
.watch_forums .node .nodeText .nodeTitle {
    margin-left: -3px;
    text-decoration: none;
}
.node .nodeText .nodeTitle,
.discussionListItems .title a,
.discussionListItems .unread .title a,
.discussionListItem .title {
    text-decoration: none;
}
.navTabs .navTab.selected .tabLinks a:hover,
.navTabs .navTab.selected .tabLinks a:focus {
    border: 1px solid transparent;
}
.message .messageContent {
    padding-top: 15px !important;
    padding-right: 30px !important;
    padding-bottom: 15px !important;
    padding-left: 15px !important;
}
.messageList .message {
    margin-bottom: 0;
}
.message .publicControls {
    margin-right: 5px;
}
.bbCodeQuote,
.quoteContainer {
    border: 1px inset rgba(255, 255, 255, .5);
}
.button {
    border-radius: 7px;
    padding: 0px 20px;
    line-height: 30px;
    background-color: rgb(0, 51, 102);
    background-image: none;
    box-shadow: 0 0 0 0 rgba(0, 0, 0, .5);
    font-size: 13px;
    font-weight: 900;
    text-align: center;
    color: #ffffff;
    text-decoration: none;
    text-shadow: 0 2px 0 #000000;
    height: 30px;
    border: 1px solid #000000;
    border-color: #000000
}
.pageNavLinkGroup {
    border-top: 1px solid rgba(0, 51, 102, .35);
    padding-top: 10px;
}
#content .pageContent {
    position: relative;
}
.tagBlock.TagContainer {
    position: absolute;
    right: 15px;
    top: 36px;
    background: rgba(28, 87, 128, 1);
    padding: 5px;
}
.titleBar h1 {
    font-size: 16pt;
}
.titleBar {
    margin-top: 20px;
    margin-bottom: 20px;
}
.PageNav a.currentPage {
    min-width: 19px;
}
.messageUserBlock a.username {
    text-decoration: none;
}
.userBanner.bannerPrimary {
    border-color: rgba(255, 255, 255, .2);
}
.button.primary {
    background-color: rgb(0, 51, 102);
}
.button:hover,
.button[href]:hover,
.buttonProxy:hover .button,
.button:active,
.button[href]:active,
.button.ToggleButton.checked,
.buttonProxy:active .button {
    color: #ffffff;
    text-decoration: none;
    background-color: rgb(0, 51, 102);
}
.privateControls a,
.publicControls a {
    font-weight: bold;
    border: 1px solid rgba(0, 0, 0, .5);
    padding: 3px 0;
    font-size: 11px;
    width: 80px;
    text-align: center;
    background-color: rgb(0, 51, 102);
}
.PageNav a:hover,
.PageNav a:focus,
.PageNav a.currentPage {
    border-color: transparent;
}
.message {
    position: relative;
}
.message .publicControls .MultiQuoteControl {
    padding-left: 0;
    padding-right: 0;
}
.onlineIndicator {
    top: 0px;
    left: 0px;
}
.nodeList .nodeTitle {
    text-decoration: none;
}
.node .nodeDescription,
.node .nodeStats {
    font-size: 12px;
}
.message .privateControls {
    float: right;
    margin-left: 0px;
    margin-right: 20px;
}
.item.control.edit {
    margin-right: 5px;
    margin-left: 0;
}
.item.control.report {
    margin-left: 0;
}
.bbCodeBlock.bbCodeQuote {
    margin-bottom: 20px;
}
.bbCodeBlock {
    margin-left: 0 !important;
}
.Menu {
    border: 0px solid rgba(255, 255, 255, .3);
    border-top: 0px;
    box-shadow: 0px 15px 25px rgba(0, 0, 0, 0.5);
}
.navPopup {
    width: 600px;
}
.discussionListItems .title a {
    font-weight: normal;
}
i.fa.fa-facebook-square,
.sharePage {
    display: none;
}
.quickReply .messageUserInfo {
    display: none;
}
aside .sidebar .section.visitorPanel {
    display: none;
}
.breadcrumb {
    border: 0;
    margin: 20px 0;
    outline: none;
}
.bbCodeQuote .quoteContainer {
    border: 0px;
}
.bbCodeQuote .attribution {
    border: 0px;
    font-weight: bold;
    border-bottom: 1px inset rgba(255, 255, 255, .5);
}
.messageInfo.primaryContent {
    border: 0px !important;
    padding-left: 10px;
}
.discussionListItems input {
    float: right;
}
}
